mapper.update:
update dca_b_academic
file_id=#{fileId}, file_url=#{fileUrl}, user_account_name=#{userAccountName}, user_account=#{userAccount}, academic_name=#{academicName}, academic_date=#{academicDate}, academic_content=#{academicContent}, state=#{state}, IS_DELETEMARK=#{isDeletemark}, CREATE_TIME=#{createTime}, MODIFY_TIME=#{modifyTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId}, auditMan=#{auditMan}, auditManName=#{auditManName}, auditDate=#{auditDate}, auditSuggestion=#{auditSuggestion}, IsUse=#{IsUse}, display_index=#{displayIndex},
where id=#{id}

mapper.select:
select  *  from dca_b_academic
where  1=1
     and dca_b_academic.id =#{ dcaBAcademic.id} 
     and dca_b_academic.file_id like concat('%', #{ dcaBAcademic.fileId} ,'%')
     and dca_b_academic.file_url like concat('%', #{ dcaBAcademic.fileUrl} ,'%')
     and dca_b_academic.user_account_name like concat('%', #{ dcaBAcademic.userAccountName} ,'%')
     and dca_b_academic.user_account like concat('%', #{ dcaBAcademic.userAccount} ,'%')
     and dca_b_academic.academic_name like concat('%', #{ dcaBAcademic.academicName} ,'%')

    AND dca_b_academic.academic_date  >=   #{dcaBAcademic.academicDateFrom}


    AND dca_b_academic.academic_date  <=   #{dcaBAcademic.academicDateTo}

     and dca_b_academic.academic_content like concat('%', #{ dcaBAcademic.academicContent} ,'%')
 and dca_b_academic.state =#{ dcaBAcademic.state} 
 and dca_b_academic.IS_DELETEMARK =#{ dcaBAcademic.isDeletemark} 

    AND dca_b_academic.CREATE_TIME  >=   #{dcaBAcademic.createTimeFrom}


    AND dca_b_academic.CREATE_TIME  <=   #{dcaBAcademic.createTimeTo}


    AND dca_b_academic.MODIFY_TIME  >=   #{dcaBAcademic.modifyTimeFrom}


    AND dca_b_academic.MODIFY_TIME  <=   #{dcaBAcademic.modifyTimeTo}

     and dca_b_academic.auditMan like concat('%', #{ dcaBAcademic.auditMan} ,'%')
     and dca_b_academic.auditManName like concat('%', #{ dcaBAcademic.auditManName} ,'%')

    AND dca_b_academic.auditDate  >=   #{dcaBAcademic.auditDateFrom}


    AND dca_b_academic.auditDate  <=   #{dcaBAcademic.auditDateTo}

     and dca_b_academic.auditSuggestion like concat('%', #{ dcaBAcademic.auditSuggestion} ,'%')
 and dca_b_academic.display_index =#{ dcaBAcademic.displayIndex}
