mapper.update:
update dca_b_attachfile
file_id=#{fileId}, display_index=#{displayIndex}, file_url=#{fileUrl}, user_account_name=#{userAccountName}, user_account=#{userAccount}, file_name=#{fileName}, file_type=#{fileType}, state=#{state}, IS_DELETEMARK=#{isDeletemark}, CREATE_TIME=#{createTime}, MODIFY_TIME=#{modifyTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId}, auditMan=#{auditMan}, auditManName=#{auditManName}, auditDate=#{auditDate}, auditSuggestion=#{auditSuggestion}, IsUse=#{IsUse},
where id=#{id}

mapper.select:
select  *  from dca_b_attachfile
where  1=1
     and dcaBAttachfile.id =#{ dcaBAttachfile.id} 
     and dcaBAttachfile.file_id like concat('%', #{ dcaBAttachfile.fileId} ,'%')
 and dcaBAttachfile.display_index =#{ dcaBAttachfile.displayIndex} 
     and dcaBAttachfile.file_url like concat('%', #{ dcaBAttachfile.fileUrl} ,'%')
     and dcaBAttachfile.user_account_name like concat('%', #{ dcaBAttachfile.userAccountName} ,'%')
     and dcaBAttachfile.user_account like concat('%', #{ dcaBAttachfile.userAccount} ,'%')
     and dcaBAttachfile.file_name like concat('%', #{ dcaBAttachfile.fileName} ,'%')
     and dcaBAttachfile.file_type like concat('%', #{ dcaBAttachfile.fileType} ,'%')
 and dcaBAttachfile.state =#{ dcaBAttachfile.state} 
 and dcaBAttachfile.IS_DELETEMARK =#{ dcaBAttachfile.isDeletemark} 

    AND dcaBAttachfile.CREATE_TIME  >=   #{dcaBAttachfile.createTimeFrom}


    AND dcaBAttachfile.CREATE_TIME  <=   #{dcaBAttachfile.createTimeTo}


    AND dcaBAttachfile.MODIFY_TIME  >=   #{dcaBAttachfile.modifyTimeFrom}


    AND dcaBAttachfile.MODIFY_TIME  <=   #{dcaBAttachfile.modifyTimeTo}

     and dcaBAttachfile.auditMan like concat('%', #{ dcaBAttachfile.auditMan} ,'%')
     and dcaBAttachfile.auditManName like concat('%', #{ dcaBAttachfile.auditManName} ,'%')

    AND dcaBAttachfile.auditDate  >=   #{dcaBAttachfile.auditDateFrom}


    AND dcaBAttachfile.auditDate  <=   #{dcaBAttachfile.auditDateTo}

     and dcaBAttachfile.auditSuggestion like concat('%', #{ dcaBAttachfile.auditSuggestion} ,'%')
