mapper.update:
update dca_d_mudules
mudule_name=#{muduleName}, state=#{state}, IS_DELETEMARK=#{isDeletemark}, CREATE_TIME=#{createTime}, MODIFY_TIME=#{modifyTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId}, parent_id=#{parentId},
where id=#{id}

mapper.select:
select  *  from dca_d_mudules
where  1=1
 and dcaDMudules.id =#{ dcaDMudules.id} 
     and dcaDMudules.mudule_name like concat('%', #{ dcaDMudules.muduleName} ,'%')
 and dcaDMudules.state =#{ dcaDMudules.state} 
 and dcaDMudules.IS_DELETEMARK =#{ dcaDMudules.isDeletemark} 

    AND dcaDMudules.CREATE_TIME  >=   #{dcaDMudules.createTimeFrom}


    AND dcaDMudules.CREATE_TIME  <=   #{dcaDMudules.createTimeTo}


    AND dcaDMudules.MODIFY_TIME  >=   #{dcaDMudules.modifyTimeFrom}


    AND dcaDMudules.MODIFY_TIME  <=   #{dcaDMudules.modifyTimeTo}

 and dcaDMudules.parent_id =#{ dcaDMudules.parentId}
