mapper.update:
update dca_b_copy_academic
dca_year=#{dcaYear}, file_id=#{fileId}, file_url=#{fileUrl}, user_account_name=#{userAccountName}, user_account=#{userAccount}, academic_name=#{academicName}, academic_date=#{academicDate}, academic_content=#{academicContent}, state=#{state}, IS_DELETEMARK=#{isDeletemark}, CREATE_TIME=#{createTime}, MODIFY_TIME=#{modifyTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId}, auditMan=#{auditMan}, auditManName=#{auditManName}, auditDate=#{auditDate}, auditSuggestion=#{auditSuggestion}, IsUse=#{IsUse}, display_index=#{displayIndex},
where id=#{id}

mapper.select:
select  *  from dca_b_copy_academic
where  1=1
     and dcaBCopyAcademic.id =#{ dcaBCopyAcademic.id} 
     and dcaBCopyAcademic.dca_year like concat('%', #{ dcaBCopyAcademic.dcaYear} ,'%')
     and dcaBCopyAcademic.file_id like concat('%', #{ dcaBCopyAcademic.fileId} ,'%')
     and dcaBCopyAcademic.file_url like concat('%', #{ dcaBCopyAcademic.fileUrl} ,'%')
     and dcaBCopyAcademic.user_account_name like concat('%', #{ dcaBCopyAcademic.userAccountName} ,'%')
     and dcaBCopyAcademic.user_account like concat('%', #{ dcaBCopyAcademic.userAccount} ,'%')
     and dcaBCopyAcademic.academic_name like concat('%', #{ dcaBCopyAcademic.academicName} ,'%')

    AND dcaBCopyAcademic.academic_date  >=   #{dcaBCopyAcademic.academicDateFrom}


    AND dcaBCopyAcademic.academic_date  <=   #{dcaBCopyAcademic.academicDateTo}

     and dcaBCopyAcademic.academic_content like concat('%', #{ dcaBCopyAcademic.academicContent} ,'%')
 and dcaBCopyAcademic.state =#{ dcaBCopyAcademic.state} 
 and dcaBCopyAcademic.IS_DELETEMARK =#{ dcaBCopyAcademic.isDeletemark} 

    AND dcaBCopyAcademic.CREATE_TIME  >=   #{dcaBCopyAcademic.createTimeFrom}


    AND dcaBCopyAcademic.CREATE_TIME  <=   #{dcaBCopyAcademic.createTimeTo}


    AND dcaBCopyAcademic.MODIFY_TIME  >=   #{dcaBCopyAcademic.modifyTimeFrom}


    AND dcaBCopyAcademic.MODIFY_TIME  <=   #{dcaBCopyAcademic.modifyTimeTo}

     and dcaBCopyAcademic.auditMan like concat('%', #{ dcaBCopyAcademic.auditMan} ,'%')
     and dcaBCopyAcademic.auditManName like concat('%', #{ dcaBCopyAcademic.auditManName} ,'%')

    AND dcaBCopyAcademic.auditDate  >=   #{dcaBCopyAcademic.auditDateFrom}


    AND dcaBCopyAcademic.auditDate  <=   #{dcaBCopyAcademic.auditDateTo}

     and dcaBCopyAcademic.auditSuggestion like concat('%', #{ dcaBCopyAcademic.auditSuggestion} ,'%')
 and dcaBCopyAcademic.display_index =#{ dcaBCopyAcademic.displayIndex}
