mapper.update:
update dca_b_copy_attachfile
dca_year=#{dcaYear}, file_id=#{fileId}, display_index=#{displayIndex}, file_url=#{fileUrl}, user_account_name=#{userAccountName}, user_account=#{userAccount}, file_name=#{fileName}, file_type=#{fileType}, state=#{state}, IS_DELETEMARK=#{isDeletemark}, CREATE_TIME=#{createTime}, MODIFY_TIME=#{modifyTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId}, auditMan=#{auditMan}, auditManName=#{auditManName}, auditDate=#{auditDate}, auditSuggestion=#{auditSuggestion}, IsUse=#{IsUse},
where id=#{id}

mapper.select:
select  *  from dca_b_copy_attachfile
where  1=1
     and dcaBCopyAttachfile.id =#{ dcaBCopyAttachfile.id} 
     and dcaBCopyAttachfile.dca_year like concat('%', #{ dcaBCopyAttachfile.dcaYear} ,'%')
     and dcaBCopyAttachfile.file_id like concat('%', #{ dcaBCopyAttachfile.fileId} ,'%')
 and dcaBCopyAttachfile.display_index =#{ dcaBCopyAttachfile.displayIndex} 
     and dcaBCopyAttachfile.file_url like concat('%', #{ dcaBCopyAttachfile.fileUrl} ,'%')
     and dcaBCopyAttachfile.user_account_name like concat('%', #{ dcaBCopyAttachfile.userAccountName} ,'%')
     and dcaBCopyAttachfile.user_account like concat('%', #{ dcaBCopyAttachfile.userAccount} ,'%')
     and dcaBCopyAttachfile.file_name like concat('%', #{ dcaBCopyAttachfile.fileName} ,'%')
     and dcaBCopyAttachfile.file_type like concat('%', #{ dcaBCopyAttachfile.fileType} ,'%')
 and dcaBCopyAttachfile.state =#{ dcaBCopyAttachfile.state} 
 and dcaBCopyAttachfile.IS_DELETEMARK =#{ dcaBCopyAttachfile.isDeletemark} 

    AND dcaBCopyAttachfile.CREATE_TIME  >=   #{dcaBCopyAttachfile.createTimeFrom}


    AND dcaBCopyAttachfile.CREATE_TIME  <=   #{dcaBCopyAttachfile.createTimeTo}


    AND dcaBCopyAttachfile.MODIFY_TIME  >=   #{dcaBCopyAttachfile.modifyTimeFrom}


    AND dcaBCopyAttachfile.MODIFY_TIME  <=   #{dcaBCopyAttachfile.modifyTimeTo}

     and dcaBCopyAttachfile.auditMan like concat('%', #{ dcaBCopyAttachfile.auditMan} ,'%')
     and dcaBCopyAttachfile.auditManName like concat('%', #{ dcaBCopyAttachfile.auditManName} ,'%')

    AND dcaBCopyAttachfile.auditDate  >=   #{dcaBCopyAttachfile.auditDateFrom}


    AND dcaBCopyAttachfile.auditDate  <=   #{dcaBCopyAttachfile.auditDateTo}

     and dcaBCopyAttachfile.auditSuggestion like concat('%', #{ dcaBCopyAttachfile.auditSuggestion} ,'%')
