mapper.update:
update dca_b_copy_auditfive
dca_year=#{dcaYear}, file_id=#{fileId}, khjg=#{khjg}, year=#{year}, display_index=#{displayIndex}, file_url=#{fileUrl}, user_account_name=#{userAccountName}, user_account=#{userAccount}, ad_content=#{adContent}, state=#{state}, IS_DELETEMARK=#{isDeletemark}, CREATE_TIME=#{createTime}, MODIFY_TIME=#{modifyTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId}, auditMan=#{auditMan}, auditManName=#{auditManName}, auditDate=#{auditDate}, auditSuggestion=#{auditSuggestion}, IsUse=#{IsUse},
where id=#{id}

mapper.select:
select  *  from dca_b_copy_auditfive
where  1=1
     and dcaBCopyAuditfive.id =#{ dcaBCopyAuditfive.id} 
     and dcaBCopyAuditfive.dca_year like concat('%', #{ dcaBCopyAuditfive.dcaYear} ,'%')
     and dcaBCopyAuditfive.file_id like concat('%', #{ dcaBCopyAuditfive.fileId} ,'%')
     and dcaBCopyAuditfive.khjg like concat('%', #{ dcaBCopyAuditfive.khjg} ,'%')
     and dcaBCopyAuditfive.year like concat('%', #{ dcaBCopyAuditfive.year} ,'%')
 and dcaBCopyAuditfive.display_index =#{ dcaBCopyAuditfive.displayIndex} 
     and dcaBCopyAuditfive.file_url like concat('%', #{ dcaBCopyAuditfive.fileUrl} ,'%')
     and dcaBCopyAuditfive.user_account_name like concat('%', #{ dcaBCopyAuditfive.userAccountName} ,'%')
     and dcaBCopyAuditfive.user_account like concat('%', #{ dcaBCopyAuditfive.userAccount} ,'%')
     and dcaBCopyAuditfive.ad_content like concat('%', #{ dcaBCopyAuditfive.adContent} ,'%')
 and dcaBCopyAuditfive.state =#{ dcaBCopyAuditfive.state} 
 and dcaBCopyAuditfive.IS_DELETEMARK =#{ dcaBCopyAuditfive.isDeletemark} 

    AND dcaBCopyAuditfive.CREATE_TIME  >=   #{dcaBCopyAuditfive.createTimeFrom}


    AND dcaBCopyAuditfive.CREATE_TIME  <=   #{dcaBCopyAuditfive.createTimeTo}


    AND dcaBCopyAuditfive.MODIFY_TIME  >=   #{dcaBCopyAuditfive.modifyTimeFrom}


    AND dcaBCopyAuditfive.MODIFY_TIME  <=   #{dcaBCopyAuditfive.modifyTimeTo}

     and dcaBCopyAuditfive.auditMan like concat('%', #{ dcaBCopyAuditfive.auditMan} ,'%')
     and dcaBCopyAuditfive.auditManName like concat('%', #{ dcaBCopyAuditfive.auditManName} ,'%')

    AND dcaBCopyAuditfive.auditDate  >=   #{dcaBCopyAuditfive.auditDateFrom}


    AND dcaBCopyAuditfive.auditDate  <=   #{dcaBCopyAuditfive.auditDateTo}

     and dcaBCopyAuditfive.auditSuggestion like concat('%', #{ dcaBCopyAuditfive.auditSuggestion} ,'%')
