mapper.update:
update dca_b_copy_auditsuggestion
dca_year=#{dcaYear}, file_id=#{fileId}, display_index=#{displayIndex}, AuditStep=#{AuditStep}, user_account_name=#{userAccountName}, user_account=#{userAccount}, pre_goal=#{preGoal}, state=#{state}, IS_DELETEMARK=#{isDeletemark}, CREATE_TIME=#{createTime}, MODIFY_TIME=#{modifyTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId}, auditMan=#{auditMan}, auditManName=#{auditManName}, auditDate=#{auditDate}, auditSuggestion=#{auditSuggestion},
where id=#{id}

mapper.select:
select  *  from dca_b_copy_auditsuggestion
where  1=1
     and dcaBCopyAuditsuggestion.id =#{ dcaBCopyAuditsuggestion.id} 
     and dcaBCopyAuditsuggestion.dca_year like concat('%', #{ dcaBCopyAuditsuggestion.dcaYear} ,'%')
     and dcaBCopyAuditsuggestion.file_id like concat('%', #{ dcaBCopyAuditsuggestion.fileId} ,'%')
 and dcaBCopyAuditsuggestion.display_index =#{ dcaBCopyAuditsuggestion.displayIndex} 
 and dcaBCopyAuditsuggestion.AuditStep =#{ dcaBCopyAuditsuggestion.AuditStep} 
     and dcaBCopyAuditsuggestion.user_account_name like concat('%', #{ dcaBCopyAuditsuggestion.userAccountName} ,'%')
     and dcaBCopyAuditsuggestion.user_account like concat('%', #{ dcaBCopyAuditsuggestion.userAccount} ,'%')
     and dcaBCopyAuditsuggestion.pre_goal like concat('%', #{ dcaBCopyAuditsuggestion.preGoal} ,'%')
 and dcaBCopyAuditsuggestion.state =#{ dcaBCopyAuditsuggestion.state} 
 and dcaBCopyAuditsuggestion.IS_DELETEMARK =#{ dcaBCopyAuditsuggestion.isDeletemark} 

    AND dcaBCopyAuditsuggestion.CREATE_TIME  >=   #{dcaBCopyAuditsuggestion.createTimeFrom}


    AND dcaBCopyAuditsuggestion.CREATE_TIME  <=   #{dcaBCopyAuditsuggestion.createTimeTo}


    AND dcaBCopyAuditsuggestion.MODIFY_TIME  >=   #{dcaBCopyAuditsuggestion.modifyTimeFrom}


    AND dcaBCopyAuditsuggestion.MODIFY_TIME  <=   #{dcaBCopyAuditsuggestion.modifyTimeTo}

     and dcaBCopyAuditsuggestion.auditMan like concat('%', #{ dcaBCopyAuditsuggestion.auditMan} ,'%')
     and dcaBCopyAuditsuggestion.auditManName like concat('%', #{ dcaBCopyAuditsuggestion.auditManName} ,'%')

    AND dcaBCopyAuditsuggestion.auditDate  >=   #{dcaBCopyAuditsuggestion.auditDateFrom}


    AND dcaBCopyAuditsuggestion.auditDate  <=   #{dcaBCopyAuditsuggestion.auditDateTo}

     and dcaBCopyAuditsuggestion.auditSuggestion like concat('%', #{ dcaBCopyAuditsuggestion.auditSuggestion} ,'%')
