mapper.update:
update dca_b_copy_educationexperice
dca_year=#{dcaYear}, file_id=#{fileId}, display_index=#{displayIndex}, file_url=#{fileUrl}, user_account_name=#{userAccountName}, user_account=#{userAccount}, exp_start_TIME=#{expStartTime}, exp_end_TIME=#{expEndTime}, exp_address=#{expAddress}, exp_school=#{expSchool}, exp_position=#{expPosition}, exp_certifier=#{expCertifier}, is_hightest=#{isHightest}, state=#{state}, IS_DELETEMARK=#{isDeletemark}, CREATE_TIME=#{createTime}, MODIFY_TIME=#{modifyTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId}, auditMan=#{auditMan}, auditManName=#{auditManName}, auditDate=#{auditDate}, auditSuggestion=#{auditSuggestion}, IsUse=#{IsUse},
where id=#{id}

mapper.select:
select  *  from dca_b_copy_educationexperice
where  1=1
     and dcaBCopyEducationexperice.id =#{ dcaBCopyEducationexperice.id} 
     and dcaBCopyEducationexperice.dca_year like concat('%', #{ dcaBCopyEducationexperice.dcaYear} ,'%')
     and dcaBCopyEducationexperice.file_id like concat('%', #{ dcaBCopyEducationexperice.fileId} ,'%')
 and dcaBCopyEducationexperice.display_index =#{ dcaBCopyEducationexperice.displayIndex} 
     and dcaBCopyEducationexperice.file_url like concat('%', #{ dcaBCopyEducationexperice.fileUrl} ,'%')
     and dcaBCopyEducationexperice.user_account_name like concat('%', #{ dcaBCopyEducationexperice.userAccountName} ,'%')
     and dcaBCopyEducationexperice.user_account like concat('%', #{ dcaBCopyEducationexperice.userAccount} ,'%')

    AND dcaBCopyEducationexperice.exp_start_TIME  >=   #{dcaBCopyEducationexperice.expStartTimeFrom}


    AND dcaBCopyEducationexperice.exp_start_TIME  <=   #{dcaBCopyEducationexperice.expStartTimeTo}


    AND dcaBCopyEducationexperice.exp_end_TIME  >=   #{dcaBCopyEducationexperice.expEndTimeFrom}


    AND dcaBCopyEducationexperice.exp_end_TIME  <=   #{dcaBCopyEducationexperice.expEndTimeTo}

     and dcaBCopyEducationexperice.exp_address like concat('%', #{ dcaBCopyEducationexperice.expAddress} ,'%')
     and dcaBCopyEducationexperice.exp_school like concat('%', #{ dcaBCopyEducationexperice.expSchool} ,'%')
     and dcaBCopyEducationexperice.exp_position like concat('%', #{ dcaBCopyEducationexperice.expPosition} ,'%')
     and dcaBCopyEducationexperice.exp_certifier like concat('%', #{ dcaBCopyEducationexperice.expCertifier} ,'%')
     and dcaBCopyEducationexperice.is_hightest like concat('%', #{ dcaBCopyEducationexperice.isHightest} ,'%')
 and dcaBCopyEducationexperice.state =#{ dcaBCopyEducationexperice.state} 
 and dcaBCopyEducationexperice.IS_DELETEMARK =#{ dcaBCopyEducationexperice.isDeletemark} 

    AND dcaBCopyEducationexperice.CREATE_TIME  >=   #{dcaBCopyEducationexperice.createTimeFrom}


    AND dcaBCopyEducationexperice.CREATE_TIME  <=   #{dcaBCopyEducationexperice.createTimeTo}


    AND dcaBCopyEducationexperice.MODIFY_TIME  >=   #{dcaBCopyEducationexperice.modifyTimeFrom}


    AND dcaBCopyEducationexperice.MODIFY_TIME  <=   #{dcaBCopyEducationexperice.modifyTimeTo}

     and dcaBCopyEducationexperice.auditMan like concat('%', #{ dcaBCopyEducationexperice.auditMan} ,'%')
     and dcaBCopyEducationexperice.auditManName like concat('%', #{ dcaBCopyEducationexperice.auditManName} ,'%')

    AND dcaBCopyEducationexperice.auditDate  >=   #{dcaBCopyEducationexperice.auditDateFrom}


    AND dcaBCopyEducationexperice.auditDate  <=   #{dcaBCopyEducationexperice.auditDateTo}

     and dcaBCopyEducationexperice.auditSuggestion like concat('%', #{ dcaBCopyEducationexperice.auditSuggestion} ,'%')
