mapper.update:
update dca_b_copy_employ
dca_year=#{dcaYear}, file_id=#{fileId}, display_index=#{displayIndex}, file_url=#{fileUrl}, user_account_name=#{userAccountName}, user_account=#{userAccount}, em_start_TIME=#{emStartTime}, em_end_TIME=#{emEndTime}, em_coursename=#{emCoursename}, em_otherwork=#{emOtherwork}, em_studentcount=#{emStudentcount}, em_weektime=#{emWeektime}, em_totaltime=#{emTotaltime}, em_content=#{emContent}, state=#{state}, IS_DELETEMARK=#{isDeletemark}, CREATE_TIME=#{createTime}, MODIFY_TIME=#{modifyTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId}, auditMan=#{auditMan}, auditManName=#{auditManName}, auditDate=#{auditDate}, auditSuggestion=#{auditSuggestion}, IsUse=#{IsUse},
where id=#{id}

mapper.select:
select  *  from dca_b_copy_employ
where  1=1
     and dcaBCopyEmploy.id =#{ dcaBCopyEmploy.id} 
     and dcaBCopyEmploy.dca_year like concat('%', #{ dcaBCopyEmploy.dcaYear} ,'%')
     and dcaBCopyEmploy.file_id like concat('%', #{ dcaBCopyEmploy.fileId} ,'%')
 and dcaBCopyEmploy.display_index =#{ dcaBCopyEmploy.displayIndex} 
     and dcaBCopyEmploy.file_url like concat('%', #{ dcaBCopyEmploy.fileUrl} ,'%')
     and dcaBCopyEmploy.user_account_name like concat('%', #{ dcaBCopyEmploy.userAccountName} ,'%')
     and dcaBCopyEmploy.user_account like concat('%', #{ dcaBCopyEmploy.userAccount} ,'%')

    AND dcaBCopyEmploy.em_start_TIME  >=   #{dcaBCopyEmploy.emStartTimeFrom}


    AND dcaBCopyEmploy.em_start_TIME  <=   #{dcaBCopyEmploy.emStartTimeTo}


    AND dcaBCopyEmploy.em_end_TIME  >=   #{dcaBCopyEmploy.emEndTimeFrom}


    AND dcaBCopyEmploy.em_end_TIME  <=   #{dcaBCopyEmploy.emEndTimeTo}

     and dcaBCopyEmploy.em_coursename like concat('%', #{ dcaBCopyEmploy.emCoursename} ,'%')
     and dcaBCopyEmploy.em_otherwork like concat('%', #{ dcaBCopyEmploy.emOtherwork} ,'%')
 and dcaBCopyEmploy.em_studentcount =#{ dcaBCopyEmploy.emStudentcount} 
     and dcaBCopyEmploy.em_content like concat('%', #{ dcaBCopyEmploy.emContent} ,'%')
 and dcaBCopyEmploy.state =#{ dcaBCopyEmploy.state} 
 and dcaBCopyEmploy.IS_DELETEMARK =#{ dcaBCopyEmploy.isDeletemark} 

    AND dcaBCopyEmploy.CREATE_TIME  >=   #{dcaBCopyEmploy.createTimeFrom}


    AND dcaBCopyEmploy.CREATE_TIME  <=   #{dcaBCopyEmploy.createTimeTo}


    AND dcaBCopyEmploy.MODIFY_TIME  >=   #{dcaBCopyEmploy.modifyTimeFrom}


    AND dcaBCopyEmploy.MODIFY_TIME  <=   #{dcaBCopyEmploy.modifyTimeTo}

     and dcaBCopyEmploy.auditMan like concat('%', #{ dcaBCopyEmploy.auditMan} ,'%')
     and dcaBCopyEmploy.auditManName like concat('%', #{ dcaBCopyEmploy.auditManName} ,'%')

    AND dcaBCopyEmploy.auditDate  >=   #{dcaBCopyEmploy.auditDateFrom}


    AND dcaBCopyEmploy.auditDate  <=   #{dcaBCopyEmploy.auditDateTo}

     and dcaBCopyEmploy.auditSuggestion like concat('%', #{ dcaBCopyEmploy.auditSuggestion} ,'%')
