mapper.update:
update dca_b_copy_essaypublish
dca_year=#{dcaYear}, file_id=#{fileId}, display_index=#{displayIndex}, file_url=#{fileUrl}, user_account_name=#{userAccountName}, user_account=#{userAccount}, essay_name=#{essayName}, eassy_journalname=#{eassyJournalname}, eassy_publishname=#{eassyPublishname}, eassy_startpage=#{eassyStartpage}, eassy_endpage=#{eassyEndpage}, eassy_journalcode=#{eassyJournalcode}, eassy_journalgrade=#{eassyJournalgrade}, eassy_publishdate=#{eassyPublishdate}, eassy_rankname=#{eassyRankname}, state=#{state}, IS_DELETEMARK=#{isDeletemark}, CREATE_TIME=#{createTime}, MODIFY_TIME=#{modifyTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId}, auditMan=#{auditMan}, auditManName=#{auditManName}, auditDate=#{auditDate}, auditSuggestion=#{auditSuggestion}, IsUse=#{IsUse},
where id=#{id}

mapper.select:
select  *  from dca_b_copy_essaypublish
where  1=1
     and dcaBCopyEssaypublish.id =#{ dcaBCopyEssaypublish.id} 
     and dcaBCopyEssaypublish.dca_year like concat('%', #{ dcaBCopyEssaypublish.dcaYear} ,'%')
     and dcaBCopyEssaypublish.file_id like concat('%', #{ dcaBCopyEssaypublish.fileId} ,'%')
 and dcaBCopyEssaypublish.display_index =#{ dcaBCopyEssaypublish.displayIndex} 
     and dcaBCopyEssaypublish.file_url like concat('%', #{ dcaBCopyEssaypublish.fileUrl} ,'%')
     and dcaBCopyEssaypublish.user_account_name like concat('%', #{ dcaBCopyEssaypublish.userAccountName} ,'%')
     and dcaBCopyEssaypublish.user_account like concat('%', #{ dcaBCopyEssaypublish.userAccount} ,'%')
     and dcaBCopyEssaypublish.essay_name like concat('%', #{ dcaBCopyEssaypublish.essayName} ,'%')
     and dcaBCopyEssaypublish.eassy_journalname like concat('%', #{ dcaBCopyEssaypublish.eassyJournalname} ,'%')
     and dcaBCopyEssaypublish.eassy_publishname like concat('%', #{ dcaBCopyEssaypublish.eassyPublishname} ,'%')
     and dcaBCopyEssaypublish.eassy_journalcode like concat('%', #{ dcaBCopyEssaypublish.eassyJournalcode} ,'%')
     and dcaBCopyEssaypublish.eassy_journalgrade like concat('%', #{ dcaBCopyEssaypublish.eassyJournalgrade} ,'%')

    AND dcaBCopyEssaypublish.eassy_publishdate  >=   #{dcaBCopyEssaypublish.eassyPublishdateFrom}


    AND dcaBCopyEssaypublish.eassy_publishdate  <=   #{dcaBCopyEssaypublish.eassyPublishdateTo}

     and dcaBCopyEssaypublish.eassy_rankname like concat('%', #{ dcaBCopyEssaypublish.eassyRankname} ,'%')
 and dcaBCopyEssaypublish.state =#{ dcaBCopyEssaypublish.state} 
 and dcaBCopyEssaypublish.IS_DELETEMARK =#{ dcaBCopyEssaypublish.isDeletemark} 

    AND dcaBCopyEssaypublish.CREATE_TIME  >=   #{dcaBCopyEssaypublish.createTimeFrom}


    AND dcaBCopyEssaypublish.CREATE_TIME  <=   #{dcaBCopyEssaypublish.createTimeTo}


    AND dcaBCopyEssaypublish.MODIFY_TIME  >=   #{dcaBCopyEssaypublish.modifyTimeFrom}


    AND dcaBCopyEssaypublish.MODIFY_TIME  <=   #{dcaBCopyEssaypublish.modifyTimeTo}

     and dcaBCopyEssaypublish.auditMan like concat('%', #{ dcaBCopyEssaypublish.auditMan} ,'%')
     and dcaBCopyEssaypublish.auditManName like concat('%', #{ dcaBCopyEssaypublish.auditManName} ,'%')

    AND dcaBCopyEssaypublish.auditDate  >=   #{dcaBCopyEssaypublish.auditDateFrom}


    AND dcaBCopyEssaypublish.auditDate  <=   #{dcaBCopyEssaypublish.auditDateTo}

     and dcaBCopyEssaypublish.auditSuggestion like concat('%', #{ dcaBCopyEssaypublish.auditSuggestion} ,'%')
