mapper.update:
update dca_b_copy_exportcountry
dca_year=#{dcaYear}, file_id=#{fileId}, display_index=#{displayIndex}, file_url=#{fileUrl}, user_account_name=#{userAccountName}, user_account=#{userAccount}, cgsj=#{cgsj}, hgsj=#{hgsj}, lxgj=#{lxgj}, lxdw=#{lxdw}, state=#{state}, IS_DELETEMARK=#{isDeletemark}, CREATE_TIME=#{createTime}, MODIFY_TIME=#{modifyTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId}, auditMan=#{auditMan}, auditManName=#{auditManName}, auditDate=#{auditDate}, auditSuggestion=#{auditSuggestion}, IsUse=#{IsUse},
where id=#{id}

mapper.select:
select  *  from dca_b_copy_exportcountry
where  1=1
     and dcaBCopyExportcountry.id =#{ dcaBCopyExportcountry.id} 
     and dcaBCopyExportcountry.dca_year like concat('%', #{ dcaBCopyExportcountry.dcaYear} ,'%')
     and dcaBCopyExportcountry.file_id like concat('%', #{ dcaBCopyExportcountry.fileId} ,'%')
 and dcaBCopyExportcountry.display_index =#{ dcaBCopyExportcountry.displayIndex} 
     and dcaBCopyExportcountry.file_url like concat('%', #{ dcaBCopyExportcountry.fileUrl} ,'%')
     and dcaBCopyExportcountry.user_account_name like concat('%', #{ dcaBCopyExportcountry.userAccountName} ,'%')
     and dcaBCopyExportcountry.user_account like concat('%', #{ dcaBCopyExportcountry.userAccount} ,'%')

    AND dcaBCopyExportcountry.cgsj  >=   #{dcaBCopyExportcountry.cgsjFrom}


    AND dcaBCopyExportcountry.cgsj  <=   #{dcaBCopyExportcountry.cgsjTo}


    AND dcaBCopyExportcountry.hgsj  >=   #{dcaBCopyExportcountry.hgsjFrom}


    AND dcaBCopyExportcountry.hgsj  <=   #{dcaBCopyExportcountry.hgsjTo}

     and dcaBCopyExportcountry.lxgj like concat('%', #{ dcaBCopyExportcountry.lxgj} ,'%')
     and dcaBCopyExportcountry.lxdw like concat('%', #{ dcaBCopyExportcountry.lxdw} ,'%')
 and dcaBCopyExportcountry.state =#{ dcaBCopyExportcountry.state} 
 and dcaBCopyExportcountry.IS_DELETEMARK =#{ dcaBCopyExportcountry.isDeletemark} 

    AND dcaBCopyExportcountry.CREATE_TIME  >=   #{dcaBCopyExportcountry.createTimeFrom}


    AND dcaBCopyExportcountry.CREATE_TIME  <=   #{dcaBCopyExportcountry.createTimeTo}


    AND dcaBCopyExportcountry.MODIFY_TIME  >=   #{dcaBCopyExportcountry.modifyTimeFrom}


    AND dcaBCopyExportcountry.MODIFY_TIME  <=   #{dcaBCopyExportcountry.modifyTimeTo}

     and dcaBCopyExportcountry.auditMan like concat('%', #{ dcaBCopyExportcountry.auditMan} ,'%')
     and dcaBCopyExportcountry.auditManName like concat('%', #{ dcaBCopyExportcountry.auditManName} ,'%')

    AND dcaBCopyExportcountry.auditDate  >=   #{dcaBCopyExportcountry.auditDateFrom}


    AND dcaBCopyExportcountry.auditDate  <=   #{dcaBCopyExportcountry.auditDateTo}

     and dcaBCopyExportcountry.auditSuggestion like concat('%', #{ dcaBCopyExportcountry.auditSuggestion} ,'%')
