mapper.update:
update dca_b_copy_fivecomment
dca_year=#{dcaYear}, file_id=#{fileId}, display_index=#{displayIndex}, file_url=#{fileUrl}, user_account_name=#{userAccountName}, user_account=#{userAccount}, five_percent=#{fivePercent}, state=#{state}, IS_DELETEMARK=#{isDeletemark}, CREATE_TIME=#{createTime}, MODIFY_TIME=#{modifyTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId}, auditMan=#{auditMan}, auditManName=#{auditManName}, auditDate=#{auditDate}, auditSuggestion=#{auditSuggestion}, IsUse=#{IsUse},
where id=#{id}

mapper.select:
select  *  from dca_b_copy_fivecomment
where  1=1
     and dcaBCopyFivecomment.id =#{ dcaBCopyFivecomment.id} 
     and dcaBCopyFivecomment.dca_year like concat('%', #{ dcaBCopyFivecomment.dcaYear} ,'%')
     and dcaBCopyFivecomment.file_id like concat('%', #{ dcaBCopyFivecomment.fileId} ,'%')
 and dcaBCopyFivecomment.display_index =#{ dcaBCopyFivecomment.displayIndex} 
     and dcaBCopyFivecomment.file_url like concat('%', #{ dcaBCopyFivecomment.fileUrl} ,'%')
     and dcaBCopyFivecomment.user_account_name like concat('%', #{ dcaBCopyFivecomment.userAccountName} ,'%')
     and dcaBCopyFivecomment.user_account like concat('%', #{ dcaBCopyFivecomment.userAccount} ,'%')
     and dcaBCopyFivecomment.five_percent like concat('%', #{ dcaBCopyFivecomment.fivePercent} ,'%')
 and dcaBCopyFivecomment.state =#{ dcaBCopyFivecomment.state} 
 and dcaBCopyFivecomment.IS_DELETEMARK =#{ dcaBCopyFivecomment.isDeletemark} 

    AND dcaBCopyFivecomment.CREATE_TIME  >=   #{dcaBCopyFivecomment.createTimeFrom}


    AND dcaBCopyFivecomment.CREATE_TIME  <=   #{dcaBCopyFivecomment.createTimeTo}


    AND dcaBCopyFivecomment.MODIFY_TIME  >=   #{dcaBCopyFivecomment.modifyTimeFrom}


    AND dcaBCopyFivecomment.MODIFY_TIME  <=   #{dcaBCopyFivecomment.modifyTimeTo}

     and dcaBCopyFivecomment.auditMan like concat('%', #{ dcaBCopyFivecomment.auditMan} ,'%')
     and dcaBCopyFivecomment.auditManName like concat('%', #{ dcaBCopyFivecomment.auditManName} ,'%')

    AND dcaBCopyFivecomment.auditDate  >=   #{dcaBCopyFivecomment.auditDateFrom}


    AND dcaBCopyFivecomment.auditDate  <=   #{dcaBCopyFivecomment.auditDateTo}

     and dcaBCopyFivecomment.auditSuggestion like concat('%', #{ dcaBCopyFivecomment.auditSuggestion} ,'%')
