mapper.update:
update dca_b_copy_graduate
dca_year=#{dcaYear}, file_id=#{fileId}, display_index=#{displayIndex}, file_url=#{fileUrl}, user_account_name=#{userAccountName}, user_account=#{userAccount}, doctor_number=#{doctorNumber}, graduate_number=#{graduateNumber}, doctor_done_number=#{doctorDoneNumber}, graduate_done_number=#{graduateDoneNumber}, prize_content=#{prizeContent}, state=#{state}, IS_DELETEMARK=#{isDeletemark}, CREATE_TIME=#{createTime}, MODIFY_TIME=#{modifyTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId}, auditMan=#{auditMan}, auditManName=#{auditManName}, auditDate=#{auditDate}, auditSuggestion=#{auditSuggestion}, IsUse=#{IsUse},
where id=#{id}

mapper.select:
select  *  from dca_b_copy_graduate
where  1=1
     and dcaBCopyGraduate.id =#{ dcaBCopyGraduate.id} 
     and dcaBCopyGraduate.dca_year like concat('%', #{ dcaBCopyGraduate.dcaYear} ,'%')
     and dcaBCopyGraduate.file_id like concat('%', #{ dcaBCopyGraduate.fileId} ,'%')
 and dcaBCopyGraduate.display_index =#{ dcaBCopyGraduate.displayIndex} 
     and dcaBCopyGraduate.file_url like concat('%', #{ dcaBCopyGraduate.fileUrl} ,'%')
     and dcaBCopyGraduate.user_account_name like concat('%', #{ dcaBCopyGraduate.userAccountName} ,'%')
     and dcaBCopyGraduate.user_account like concat('%', #{ dcaBCopyGraduate.userAccount} ,'%')
 and dcaBCopyGraduate.doctor_number =#{ dcaBCopyGraduate.doctorNumber} 
 and dcaBCopyGraduate.graduate_number =#{ dcaBCopyGraduate.graduateNumber} 
 and dcaBCopyGraduate.doctor_done_number =#{ dcaBCopyGraduate.doctorDoneNumber} 
 and dcaBCopyGraduate.graduate_done_number =#{ dcaBCopyGraduate.graduateDoneNumber} 
     and dcaBCopyGraduate.prize_content like concat('%', #{ dcaBCopyGraduate.prizeContent} ,'%')
 and dcaBCopyGraduate.state =#{ dcaBCopyGraduate.state} 
 and dcaBCopyGraduate.IS_DELETEMARK =#{ dcaBCopyGraduate.isDeletemark} 

    AND dcaBCopyGraduate.CREATE_TIME  >=   #{dcaBCopyGraduate.createTimeFrom}


    AND dcaBCopyGraduate.CREATE_TIME  <=   #{dcaBCopyGraduate.createTimeTo}


    AND dcaBCopyGraduate.MODIFY_TIME  >=   #{dcaBCopyGraduate.modifyTimeFrom}


    AND dcaBCopyGraduate.MODIFY_TIME  <=   #{dcaBCopyGraduate.modifyTimeTo}

     and dcaBCopyGraduate.auditMan like concat('%', #{ dcaBCopyGraduate.auditMan} ,'%')
     and dcaBCopyGraduate.auditManName like concat('%', #{ dcaBCopyGraduate.auditManName} ,'%')

    AND dcaBCopyGraduate.auditDate  >=   #{dcaBCopyGraduate.auditDateFrom}


    AND dcaBCopyGraduate.auditDate  <=   #{dcaBCopyGraduate.auditDateTo}

     and dcaBCopyGraduate.auditSuggestion like concat('%', #{ dcaBCopyGraduate.auditSuggestion} ,'%')
