mapper.update:
update dca_b_copy_innovatebuild
dca_year=#{dcaYear}, file_id=#{fileId}, display_index=#{displayIndex}, file_url=#{fileUrl}, user_account_name=#{userAccountName}, user_account=#{userAccount}, project_name=#{projectName}, project_type=#{projectType}, project_source=#{projectSource}, contract_fund=#{contractFund}, real_fund=#{realFund}, audit_date=#{auditDate2}, start_date=#{startDate}, end_date=#{endDate}, rank_num=#{rankNum}, state=#{state}, audit_state=#{auditState}, IS_DELETEMARK=#{isDeletemark}, CREATE_TIME=#{createTime}, MODIFY_TIME=#{modifyTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId}, auditMan=#{auditMan}, auditManName=#{auditManName}, auditDate=#{auditDate}, auditSuggestion=#{auditSuggestion}, IsUse=#{IsUse},
where id=#{id}

mapper.select:
select  *  from dca_b_copy_innovatebuild
where  1=1
     and dcaBCopyInnovatebuild.id =#{ dcaBCopyInnovatebuild.id} 
     and dcaBCopyInnovatebuild.dca_year like concat('%', #{ dcaBCopyInnovatebuild.dcaYear} ,'%')
     and dcaBCopyInnovatebuild.file_id like concat('%', #{ dcaBCopyInnovatebuild.fileId} ,'%')
 and dcaBCopyInnovatebuild.display_index =#{ dcaBCopyInnovatebuild.displayIndex} 
     and dcaBCopyInnovatebuild.file_url like concat('%', #{ dcaBCopyInnovatebuild.fileUrl} ,'%')
     and dcaBCopyInnovatebuild.user_account_name like concat('%', #{ dcaBCopyInnovatebuild.userAccountName} ,'%')
     and dcaBCopyInnovatebuild.user_account like concat('%', #{ dcaBCopyInnovatebuild.userAccount} ,'%')
     and dcaBCopyInnovatebuild.project_name like concat('%', #{ dcaBCopyInnovatebuild.projectName} ,'%')
     and dcaBCopyInnovatebuild.project_type like concat('%', #{ dcaBCopyInnovatebuild.projectType} ,'%')
     and dcaBCopyInnovatebuild.project_source like concat('%', #{ dcaBCopyInnovatebuild.projectSource} ,'%')


    AND dcaBCopyInnovatebuild.start_date  >=   #{dcaBCopyInnovatebuild.startDateFrom}


    AND dcaBCopyInnovatebuild.start_date  <=   #{dcaBCopyInnovatebuild.startDateTo}


    AND dcaBCopyInnovatebuild.end_date  >=   #{dcaBCopyInnovatebuild.endDateFrom}


    AND dcaBCopyInnovatebuild.end_date  <=   #{dcaBCopyInnovatebuild.endDateTo}

 and dcaBCopyInnovatebuild.rank_num =#{ dcaBCopyInnovatebuild.rankNum} 
 and dcaBCopyInnovatebuild.state =#{ dcaBCopyInnovatebuild.state} 
 and dcaBCopyInnovatebuild.audit_state =#{ dcaBCopyInnovatebuild.auditState} 
 and dcaBCopyInnovatebuild.IS_DELETEMARK =#{ dcaBCopyInnovatebuild.isDeletemark} 

    AND dcaBCopyInnovatebuild.CREATE_TIME  >=   #{dcaBCopyInnovatebuild.createTimeFrom}


    AND dcaBCopyInnovatebuild.CREATE_TIME  <=   #{dcaBCopyInnovatebuild.createTimeTo}


    AND dcaBCopyInnovatebuild.MODIFY_TIME  >=   #{dcaBCopyInnovatebuild.modifyTimeFrom}


    AND dcaBCopyInnovatebuild.MODIFY_TIME  <=   #{dcaBCopyInnovatebuild.modifyTimeTo}

     and dcaBCopyInnovatebuild.auditMan like concat('%', #{ dcaBCopyInnovatebuild.auditMan} ,'%')
     and dcaBCopyInnovatebuild.auditManName like concat('%', #{ dcaBCopyInnovatebuild.auditManName} ,'%')

    AND dcaBCopyInnovatebuild.auditDate  >=   #{dcaBCopyInnovatebuild.auditDateFrom}


    AND dcaBCopyInnovatebuild.auditDate  <=   #{dcaBCopyInnovatebuild.auditDateTo}

     and dcaBCopyInnovatebuild.auditSuggestion like concat('%', #{ dcaBCopyInnovatebuild.auditSuggestion} ,'%')
