mapper.update:
update dca_b_copy_otherwork
dca_year=#{dcaYear}, file_id=#{fileId}, display_index=#{displayIndex}, file_url=#{fileUrl}, user_account_name=#{userAccountName}, user_account=#{userAccount}, other_work=#{otherWork}, state=#{state}, IS_DELETEMARK=#{isDeletemark}, CREATE_TIME=#{createTime}, MODIFY_TIME=#{modifyTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId}, auditMan=#{auditMan}, auditManName=#{auditManName}, auditDate=#{auditDate}, auditSuggestion=#{auditSuggestion}, IsUse=#{IsUse},
where id=#{id}

mapper.select:
select  *  from dca_b_copy_otherwork
where  1=1
     and dcaBCopyOtherwork.id =#{ dcaBCopyOtherwork.id} 
     and dcaBCopyOtherwork.dca_year like concat('%', #{ dcaBCopyOtherwork.dcaYear} ,'%')
     and dcaBCopyOtherwork.file_id like concat('%', #{ dcaBCopyOtherwork.fileId} ,'%')
 and dcaBCopyOtherwork.display_index =#{ dcaBCopyOtherwork.displayIndex} 
     and dcaBCopyOtherwork.file_url like concat('%', #{ dcaBCopyOtherwork.fileUrl} ,'%')
     and dcaBCopyOtherwork.user_account_name like concat('%', #{ dcaBCopyOtherwork.userAccountName} ,'%')
     and dcaBCopyOtherwork.user_account like concat('%', #{ dcaBCopyOtherwork.userAccount} ,'%')
     and dcaBCopyOtherwork.other_work like concat('%', #{ dcaBCopyOtherwork.otherWork} ,'%')
 and dcaBCopyOtherwork.state =#{ dcaBCopyOtherwork.state} 
 and dcaBCopyOtherwork.IS_DELETEMARK =#{ dcaBCopyOtherwork.isDeletemark} 

    AND dcaBCopyOtherwork.CREATE_TIME  >=   #{dcaBCopyOtherwork.createTimeFrom}


    AND dcaBCopyOtherwork.CREATE_TIME  <=   #{dcaBCopyOtherwork.createTimeTo}


    AND dcaBCopyOtherwork.MODIFY_TIME  >=   #{dcaBCopyOtherwork.modifyTimeFrom}


    AND dcaBCopyOtherwork.MODIFY_TIME  <=   #{dcaBCopyOtherwork.modifyTimeTo}

     and dcaBCopyOtherwork.auditMan like concat('%', #{ dcaBCopyOtherwork.auditMan} ,'%')
     and dcaBCopyOtherwork.auditManName like concat('%', #{ dcaBCopyOtherwork.auditManName} ,'%')

    AND dcaBCopyOtherwork.auditDate  >=   #{dcaBCopyOtherwork.auditDateFrom}


    AND dcaBCopyOtherwork.auditDate  <=   #{dcaBCopyOtherwork.auditDateTo}

     and dcaBCopyOtherwork.auditSuggestion like concat('%', #{ dcaBCopyOtherwork.auditSuggestion} ,'%')
