mapper.update:
update dca_b_copy_paperspublish
dca_year=#{dcaYear}, file_id=#{fileId}, display_index=#{displayIndex}, file_url=#{fileUrl}, user_account_name=#{userAccountName}, user_account=#{userAccount}, paper_name=#{paperName}, journal_name=#{journalName}, journal_code=#{journalCode}, paper_publishdate=#{paperPublishdate}, paper_shoulu=#{paperShoulu}, paper_cause=#{paperCause}, is_best=#{isBest}, other_times=#{otherTimes}, author_rank=#{authorRank}, state=#{state}, IS_DELETEMARK=#{isDeletemark}, CREATE_TIME=#{createTime}, MODIFY_TIME=#{modifyTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId}, auditMan=#{auditMan}, auditManName=#{auditManName}, auditDate=#{auditDate}, auditSuggestion=#{auditSuggestion}, IsUse=#{IsUse},
where id=#{id}

mapper.select:
select  *  from dca_b_copy_paperspublish
where  1=1
     and dcaBCopyPaperspublish.id =#{ dcaBCopyPaperspublish.id} 
     and dcaBCopyPaperspublish.dca_year like concat('%', #{ dcaBCopyPaperspublish.dcaYear} ,'%')
     and dcaBCopyPaperspublish.file_id like concat('%', #{ dcaBCopyPaperspublish.fileId} ,'%')
 and dcaBCopyPaperspublish.display_index =#{ dcaBCopyPaperspublish.displayIndex} 
     and dcaBCopyPaperspublish.file_url like concat('%', #{ dcaBCopyPaperspublish.fileUrl} ,'%')
     and dcaBCopyPaperspublish.user_account_name like concat('%', #{ dcaBCopyPaperspublish.userAccountName} ,'%')
     and dcaBCopyPaperspublish.user_account like concat('%', #{ dcaBCopyPaperspublish.userAccount} ,'%')
     and dcaBCopyPaperspublish.paper_name like concat('%', #{ dcaBCopyPaperspublish.paperName} ,'%')
     and dcaBCopyPaperspublish.journal_name like concat('%', #{ dcaBCopyPaperspublish.journalName} ,'%')
     and dcaBCopyPaperspublish.journal_code like concat('%', #{ dcaBCopyPaperspublish.journalCode} ,'%')

    AND dcaBCopyPaperspublish.paper_publishdate  >=   #{dcaBCopyPaperspublish.paperPublishdateFrom}


    AND dcaBCopyPaperspublish.paper_publishdate  <=   #{dcaBCopyPaperspublish.paperPublishdateTo}

     and dcaBCopyPaperspublish.paper_shoulu like concat('%', #{ dcaBCopyPaperspublish.paperShoulu} ,'%')
     and dcaBCopyPaperspublish.paper_cause like concat('%', #{ dcaBCopyPaperspublish.paperCause} ,'%')
     and dcaBCopyPaperspublish.is_best like concat('%', #{ dcaBCopyPaperspublish.isBest} ,'%')
     and dcaBCopyPaperspublish.other_times like concat('%', #{ dcaBCopyPaperspublish.otherTimes} ,'%')
     and dcaBCopyPaperspublish.author_rank like concat('%', #{ dcaBCopyPaperspublish.authorRank} ,'%')
 and dcaBCopyPaperspublish.state =#{ dcaBCopyPaperspublish.state} 
 and dcaBCopyPaperspublish.IS_DELETEMARK =#{ dcaBCopyPaperspublish.isDeletemark} 

    AND dcaBCopyPaperspublish.CREATE_TIME  >=   #{dcaBCopyPaperspublish.createTimeFrom}


    AND dcaBCopyPaperspublish.CREATE_TIME  <=   #{dcaBCopyPaperspublish.createTimeTo}


    AND dcaBCopyPaperspublish.MODIFY_TIME  >=   #{dcaBCopyPaperspublish.modifyTimeFrom}


    AND dcaBCopyPaperspublish.MODIFY_TIME  <=   #{dcaBCopyPaperspublish.modifyTimeTo}

     and dcaBCopyPaperspublish.auditMan like concat('%', #{ dcaBCopyPaperspublish.auditMan} ,'%')
     and dcaBCopyPaperspublish.auditManName like concat('%', #{ dcaBCopyPaperspublish.auditManName} ,'%')

    AND dcaBCopyPaperspublish.auditDate  >=   #{dcaBCopyPaperspublish.auditDateFrom}


    AND dcaBCopyPaperspublish.auditDate  <=   #{dcaBCopyPaperspublish.auditDateTo}

     and dcaBCopyPaperspublish.auditSuggestion like concat('%', #{ dcaBCopyPaperspublish.auditSuggestion} ,'%')
