mapper.update:
update dca_b_copy_parttimejob
dca_year=#{dcaYear}, file_id=#{fileId}, display_index=#{displayIndex}, file_url=#{fileUrl}, user_account_name=#{userAccountName}, user_account=#{userAccount}, jz_start_time=#{jzStartTime}, jz_end_time=#{jzEndTime}, jz_zw=#{jzZw}, jz_content=#{jzContent}, state=#{state}, IS_DELETEMARK=#{isDeletemark}, CREATE_TIME=#{createTime}, MODIFY_TIME=#{modifyTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId}, auditMan=#{auditMan}, auditManName=#{auditManName}, auditDate=#{auditDate}, auditSuggestion=#{auditSuggestion}, IsUse=#{IsUse},
where id=#{id}

mapper.select:
select  *  from dca_b_copy_parttimejob
where  1=1
     and dca_b_copy_parttimejob.id =#{ dcaBCopyParttimejob.id} 
     and dca_b_copy_parttimejob.dca_year like concat('%', #{ dcaBCopyParttimejob.dcaYear} ,'%')
     and dca_b_copy_parttimejob.file_id like concat('%', #{ dcaBCopyParttimejob.fileId} ,'%')
 and dca_b_copy_parttimejob.display_index =#{ dcaBCopyParttimejob.displayIndex} 
     and dca_b_copy_parttimejob.file_url like concat('%', #{ dcaBCopyParttimejob.fileUrl} ,'%')
     and dca_b_copy_parttimejob.user_account_name like concat('%', #{ dcaBCopyParttimejob.userAccountName} ,'%')
     and dca_b_copy_parttimejob.user_account like concat('%', #{ dcaBCopyParttimejob.userAccount} ,'%')

    AND dca_b_copy_parttimejob.jz_start_time  >=   #{dcaBCopyParttimejob.jzStartTimeFrom}


    AND dca_b_copy_parttimejob.jz_start_time  <=   #{dcaBCopyParttimejob.jzStartTimeTo}


    AND dca_b_copy_parttimejob.jz_end_time  >=   #{dcaBCopyParttimejob.jzEndTimeFrom}


    AND dca_b_copy_parttimejob.jz_end_time  <=   #{dcaBCopyParttimejob.jzEndTimeTo}

     and dca_b_copy_parttimejob.jz_zw like concat('%', #{ dcaBCopyParttimejob.jzZw} ,'%')
     and dca_b_copy_parttimejob.jz_content like concat('%', #{ dcaBCopyParttimejob.jzContent} ,'%')
 and dca_b_copy_parttimejob.state =#{ dcaBCopyParttimejob.state} 
 and dca_b_copy_parttimejob.IS_DELETEMARK =#{ dcaBCopyParttimejob.isDeletemark} 

    AND dca_b_copy_parttimejob.CREATE_TIME  >=   #{dcaBCopyParttimejob.createTimeFrom}


    AND dca_b_copy_parttimejob.CREATE_TIME  <=   #{dcaBCopyParttimejob.createTimeTo}


    AND dca_b_copy_parttimejob.MODIFY_TIME  >=   #{dcaBCopyParttimejob.modifyTimeFrom}


    AND dca_b_copy_parttimejob.MODIFY_TIME  <=   #{dcaBCopyParttimejob.modifyTimeTo}

     and dca_b_copy_parttimejob.auditMan like concat('%', #{ dcaBCopyParttimejob.auditMan} ,'%')
     and dca_b_copy_parttimejob.auditManName like concat('%', #{ dcaBCopyParttimejob.auditManName} ,'%')

    AND dca_b_copy_parttimejob.auditDate  >=   #{dcaBCopyParttimejob.auditDateFrom}


    AND dca_b_copy_parttimejob.auditDate  <=   #{dcaBCopyParttimejob.auditDateTo}

     and dca_b_copy_parttimejob.auditSuggestion like concat('%', #{ dcaBCopyParttimejob.auditSuggestion} ,'%')
