mapper.update:
update dca_b_copy_publicarticle
dca_year=#{dcaYear}, file_id=#{fileId}, display_index=#{displayIndex}, file_url=#{fileUrl}, user_account_name=#{userAccountName}, user_account=#{userAccount}, zzlx=#{zzlx}, zzmc=#{zzmc}, zzsf=#{zzsf}, cb_date=#{cbDate}, ranknum=#{ranknum}, printnum=#{printnum}, book_no=#{bookNo}, cbsmc=#{cbsmc}, bxzjmc=#{bxzjmc}, bxwzqzy=#{bxwzqzy}, bxwzzshj=#{bxwzzshj}, cdzs=#{cdzs}, audit_state=#{auditState}, state=#{state}, audit_xuhao=#{auditXuhao}, IS_DELETEMARK=#{isDeletemark}, CREATE_TIME=#{createTime}, MODIFY_TIME=#{modifyTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId}, auditMan=#{auditMan}, auditManName=#{auditManName}, auditDate=#{auditDate}, auditSuggestion=#{auditSuggestion}, IsUse=#{IsUse},
where id=#{id}

mapper.select:
select  *  from dca_b_copy_publicarticle
where  1=1
     and dcaBCopyPublicarticle.id =#{ dcaBCopyPublicarticle.id} 
     and dcaBCopyPublicarticle.dca_year like concat('%', #{ dcaBCopyPublicarticle.dcaYear} ,'%')
     and dcaBCopyPublicarticle.file_id like concat('%', #{ dcaBCopyPublicarticle.fileId} ,'%')
 and dcaBCopyPublicarticle.display_index =#{ dcaBCopyPublicarticle.displayIndex} 
     and dcaBCopyPublicarticle.file_url like concat('%', #{ dcaBCopyPublicarticle.fileUrl} ,'%')
     and dcaBCopyPublicarticle.user_account_name like concat('%', #{ dcaBCopyPublicarticle.userAccountName} ,'%')
     and dcaBCopyPublicarticle.user_account like concat('%', #{ dcaBCopyPublicarticle.userAccount} ,'%')
     and dcaBCopyPublicarticle.zzlx like concat('%', #{ dcaBCopyPublicarticle.zzlx} ,'%')
     and dcaBCopyPublicarticle.zzmc like concat('%', #{ dcaBCopyPublicarticle.zzmc} ,'%')
     and dcaBCopyPublicarticle.zzsf like concat('%', #{ dcaBCopyPublicarticle.zzsf} ,'%')

    AND dcaBCopyPublicarticle.cb_date  >=   #{dcaBCopyPublicarticle.cbDateFrom}


    AND dcaBCopyPublicarticle.cb_date  <=   #{dcaBCopyPublicarticle.cbDateTo}

 and dcaBCopyPublicarticle.ranknum =#{ dcaBCopyPublicarticle.ranknum} 
 and dcaBCopyPublicarticle.printnum =#{ dcaBCopyPublicarticle.printnum} 
     and dcaBCopyPublicarticle.book_no like concat('%', #{ dcaBCopyPublicarticle.bookNo} ,'%')
     and dcaBCopyPublicarticle.cbsmc like concat('%', #{ dcaBCopyPublicarticle.cbsmc} ,'%')
     and dcaBCopyPublicarticle.bxzjmc like concat('%', #{ dcaBCopyPublicarticle.bxzjmc} ,'%')
     and dcaBCopyPublicarticle.bxwzqzy like concat('%', #{ dcaBCopyPublicarticle.bxwzqzy} ,'%')
 and dcaBCopyPublicarticle.audit_state =#{ dcaBCopyPublicarticle.auditState} 
 and dcaBCopyPublicarticle.state =#{ dcaBCopyPublicarticle.state} 
 and dcaBCopyPublicarticle.audit_xuhao =#{ dcaBCopyPublicarticle.auditXuhao} 
 and dcaBCopyPublicarticle.IS_DELETEMARK =#{ dcaBCopyPublicarticle.isDeletemark} 

    AND dcaBCopyPublicarticle.CREATE_TIME  >=   #{dcaBCopyPublicarticle.createTimeFrom}


    AND dcaBCopyPublicarticle.CREATE_TIME  <=   #{dcaBCopyPublicarticle.createTimeTo}


    AND dcaBCopyPublicarticle.MODIFY_TIME  >=   #{dcaBCopyPublicarticle.modifyTimeFrom}


    AND dcaBCopyPublicarticle.MODIFY_TIME  <=   #{dcaBCopyPublicarticle.modifyTimeTo}

     and dcaBCopyPublicarticle.auditMan like concat('%', #{ dcaBCopyPublicarticle.auditMan} ,'%')
     and dcaBCopyPublicarticle.auditManName like concat('%', #{ dcaBCopyPublicarticle.auditManName} ,'%')

    AND dcaBCopyPublicarticle.auditDate  >=   #{dcaBCopyPublicarticle.auditDateFrom}


    AND dcaBCopyPublicarticle.auditDate  <=   #{dcaBCopyPublicarticle.auditDateTo}

     and dcaBCopyPublicarticle.auditSuggestion like concat('%', #{ dcaBCopyPublicarticle.auditSuggestion} ,'%')
