mapper.update:
update dca_b_copy_sciachievement
dca_year=#{dcaYear}, file_id=#{fileId}, file_url=#{fileUrl}, user_account_name=#{userAccountName}, user_account=#{userAccount}, achievement_name=#{achievementName}, rank_index=#{rankIndex}, achievement_grade=#{achievementGrade}, achievement_date=#{achievementDate}, achievement_define=#{achievementDefine}, achievement_content=#{achievementContent}, state=#{state}, IS_DELETEMARK=#{isDeletemark}, CREATE_TIME=#{createTime}, MODIFY_TIME=#{modifyTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId}, auditMan=#{auditMan}, auditManName=#{auditManName}, auditDate=#{auditDate}, auditSuggestion=#{auditSuggestion}, IsUse=#{IsUse}, display_index=#{displayIndex},
where id=#{id}

mapper.select:
select  *  from dca_b_copy_sciachievement
where  1=1
     and dca_b_copy_sciachievement.id =#{ dcaBCopySciachievement.id} 
     and dca_b_copy_sciachievement.dca_year like concat('%', #{ dcaBCopySciachievement.dcaYear} ,'%')
     and dca_b_copy_sciachievement.file_id like concat('%', #{ dcaBCopySciachievement.fileId} ,'%')
     and dca_b_copy_sciachievement.file_url like concat('%', #{ dcaBCopySciachievement.fileUrl} ,'%')
     and dca_b_copy_sciachievement.user_account_name like concat('%', #{ dcaBCopySciachievement.userAccountName} ,'%')
     and dca_b_copy_sciachievement.user_account like concat('%', #{ dcaBCopySciachievement.userAccount} ,'%')
     and dca_b_copy_sciachievement.achievement_name like concat('%', #{ dcaBCopySciachievement.achievementName} ,'%')
 and dca_b_copy_sciachievement.rank_index =#{ dcaBCopySciachievement.rankIndex} 
     and dca_b_copy_sciachievement.achievement_grade like concat('%', #{ dcaBCopySciachievement.achievementGrade} ,'%')

    AND dca_b_copy_sciachievement.achievement_date  >=   #{dcaBCopySciachievement.achievementDateFrom}


    AND dca_b_copy_sciachievement.achievement_date  <=   #{dcaBCopySciachievement.achievementDateTo}

     and dca_b_copy_sciachievement.achievement_define like concat('%', #{ dcaBCopySciachievement.achievementDefine} ,'%')
     and dca_b_copy_sciachievement.achievement_content like concat('%', #{ dcaBCopySciachievement.achievementContent} ,'%')
 and dca_b_copy_sciachievement.state =#{ dcaBCopySciachievement.state} 
 and dca_b_copy_sciachievement.IS_DELETEMARK =#{ dcaBCopySciachievement.isDeletemark} 

    AND dca_b_copy_sciachievement.CREATE_TIME  >=   #{dcaBCopySciachievement.createTimeFrom}


    AND dca_b_copy_sciachievement.CREATE_TIME  <=   #{dcaBCopySciachievement.createTimeTo}


    AND dca_b_copy_sciachievement.MODIFY_TIME  >=   #{dcaBCopySciachievement.modifyTimeFrom}


    AND dca_b_copy_sciachievement.MODIFY_TIME  <=   #{dcaBCopySciachievement.modifyTimeTo}

     and dca_b_copy_sciachievement.auditMan like concat('%', #{ dcaBCopySciachievement.auditMan} ,'%')
     and dca_b_copy_sciachievement.auditManName like concat('%', #{ dcaBCopySciachievement.auditManName} ,'%')

    AND dca_b_copy_sciachievement.auditDate  >=   #{dcaBCopySciachievement.auditDateFrom}


    AND dca_b_copy_sciachievement.auditDate  <=   #{dcaBCopySciachievement.auditDateTo}

     and dca_b_copy_sciachievement.auditSuggestion like concat('%', #{ dcaBCopySciachievement.auditSuggestion} ,'%')
 and dca_b_copy_sciachievement.display_index =#{ dcaBCopySciachievement.displayIndex}
