mapper.update:
update dca_b_copy_sureachievement
dca_year=#{dcaYear}, file_id=#{fileId}, file_url=#{fileUrl}, user_account_name=#{userAccountName}, user_account=#{userAccount}, achievement_name=#{achievementName}, rank_index=#{rankIndex}, achievement_grade=#{achievementGrade}, achievement_date=#{achievementDate}, achievement_define=#{achievementDefine}, achievement_content=#{achievementContent}, state=#{state}, IS_DELETEMARK=#{isDeletemark}, CREATE_TIME=#{createTime}, MODIFY_TIME=#{modifyTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId}, auditMan=#{auditMan}, auditManName=#{auditManName}, auditDate=#{auditDate}, auditSuggestion=#{auditSuggestion}, IsUse=#{IsUse}, display_index=#{displayIndex},
where id=#{id}

mapper.select:
select  *  from dca_b_copy_sureachievement
where  1=1
     and dca_b_copy_sureachievement.id =#{ dcaBCopySureachievement.id} 
     and dca_b_copy_sureachievement.dca_year like concat('%', #{ dcaBCopySureachievement.dcaYear} ,'%')
     and dca_b_copy_sureachievement.file_id like concat('%', #{ dcaBCopySureachievement.fileId} ,'%')
     and dca_b_copy_sureachievement.file_url like concat('%', #{ dcaBCopySureachievement.fileUrl} ,'%')
     and dca_b_copy_sureachievement.user_account_name like concat('%', #{ dcaBCopySureachievement.userAccountName} ,'%')
     and dca_b_copy_sureachievement.user_account like concat('%', #{ dcaBCopySureachievement.userAccount} ,'%')
     and dca_b_copy_sureachievement.achievement_name like concat('%', #{ dcaBCopySureachievement.achievementName} ,'%')
 and dca_b_copy_sureachievement.rank_index =#{ dcaBCopySureachievement.rankIndex} 
     and dca_b_copy_sureachievement.achievement_grade like concat('%', #{ dcaBCopySureachievement.achievementGrade} ,'%')

    AND dca_b_copy_sureachievement.achievement_date  >=   #{dcaBCopySureachievement.achievementDateFrom}


    AND dca_b_copy_sureachievement.achievement_date  <=   #{dcaBCopySureachievement.achievementDateTo}

     and dca_b_copy_sureachievement.achievement_define like concat('%', #{ dcaBCopySureachievement.achievementDefine} ,'%')
     and dca_b_copy_sureachievement.achievement_content like concat('%', #{ dcaBCopySureachievement.achievementContent} ,'%')
 and dca_b_copy_sureachievement.state =#{ dcaBCopySureachievement.state} 
 and dca_b_copy_sureachievement.IS_DELETEMARK =#{ dcaBCopySureachievement.isDeletemark} 

    AND dca_b_copy_sureachievement.CREATE_TIME  >=   #{dcaBCopySureachievement.createTimeFrom}


    AND dca_b_copy_sureachievement.CREATE_TIME  <=   #{dcaBCopySureachievement.createTimeTo}


    AND dca_b_copy_sureachievement.MODIFY_TIME  >=   #{dcaBCopySureachievement.modifyTimeFrom}


    AND dca_b_copy_sureachievement.MODIFY_TIME  <=   #{dcaBCopySureachievement.modifyTimeTo}

     and dca_b_copy_sureachievement.auditMan like concat('%', #{ dcaBCopySureachievement.auditMan} ,'%')
     and dca_b_copy_sureachievement.auditManName like concat('%', #{ dcaBCopySureachievement.auditManName} ,'%')

    AND dca_b_copy_sureachievement.auditDate  >=   #{dcaBCopySureachievement.auditDateFrom}


    AND dca_b_copy_sureachievement.auditDate  <=   #{dcaBCopySureachievement.auditDateTo}

     and dca_b_copy_sureachievement.auditSuggestion like concat('%', #{ dcaBCopySureachievement.auditSuggestion} ,'%')
 and dca_b_copy_sureachievement.display_index =#{ dcaBCopySureachievement.displayIndex}
