mapper.update:
update dca_b_copy_talent
dca_year=#{dcaYear}, file_id=#{fileId}, display_index=#{displayIndex}, file_url=#{fileUrl}, user_account_name=#{userAccountName}, user_account=#{userAccount}, talet_name=#{taletName}, talent_start_date=#{talentStartDate}, talent_end_date=#{talentEndDate}, talent_type=#{talentType}, student_number=#{studentNumber}, total_time=#{totalTime}, person_time=#{personTime}, state=#{state}, IS_DELETEMARK=#{isDeletemark}, CREATE_TIME=#{createTime}, MODIFY_TIME=#{modifyTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId}, auditMan=#{auditMan}, auditManName=#{auditManName}, auditDate=#{auditDate}, auditSuggestion=#{auditSuggestion}, IsUse=#{IsUse},
where id=#{id}

mapper.select:
select  *  from dca_b_copy_talent
where  1=1
     and dcaBCopyTalent.id =#{ dcaBCopyTalent.id} 
     and dcaBCopyTalent.dca_year like concat('%', #{ dcaBCopyTalent.dcaYear} ,'%')
     and dcaBCopyTalent.file_id like concat('%', #{ dcaBCopyTalent.fileId} ,'%')
 and dcaBCopyTalent.display_index =#{ dcaBCopyTalent.displayIndex} 
     and dcaBCopyTalent.file_url like concat('%', #{ dcaBCopyTalent.fileUrl} ,'%')
     and dcaBCopyTalent.user_account_name like concat('%', #{ dcaBCopyTalent.userAccountName} ,'%')
     and dcaBCopyTalent.user_account like concat('%', #{ dcaBCopyTalent.userAccount} ,'%')
     and dcaBCopyTalent.talet_name like concat('%', #{ dcaBCopyTalent.taletName} ,'%')

    AND dcaBCopyTalent.talent_start_date  >=   #{dcaBCopyTalent.talentStartDateFrom}


    AND dcaBCopyTalent.talent_start_date  <=   #{dcaBCopyTalent.talentStartDateTo}


    AND dcaBCopyTalent.talent_end_date  >=   #{dcaBCopyTalent.talentEndDateFrom}


    AND dcaBCopyTalent.talent_end_date  <=   #{dcaBCopyTalent.talentEndDateTo}

     and dcaBCopyTalent.talent_type like concat('%', #{ dcaBCopyTalent.talentType} ,'%')
 and dcaBCopyTalent.student_number =#{ dcaBCopyTalent.studentNumber} 
 and dcaBCopyTalent.state =#{ dcaBCopyTalent.state} 
 and dcaBCopyTalent.IS_DELETEMARK =#{ dcaBCopyTalent.isDeletemark} 

    AND dcaBCopyTalent.CREATE_TIME  >=   #{dcaBCopyTalent.createTimeFrom}


    AND dcaBCopyTalent.CREATE_TIME  <=   #{dcaBCopyTalent.createTimeTo}


    AND dcaBCopyTalent.MODIFY_TIME  >=   #{dcaBCopyTalent.modifyTimeFrom}


    AND dcaBCopyTalent.MODIFY_TIME  <=   #{dcaBCopyTalent.modifyTimeTo}

     and dcaBCopyTalent.auditMan like concat('%', #{ dcaBCopyTalent.auditMan} ,'%')
     and dcaBCopyTalent.auditManName like concat('%', #{ dcaBCopyTalent.auditManName} ,'%')

    AND dcaBCopyTalent.auditDate  >=   #{dcaBCopyTalent.auditDateFrom}


    AND dcaBCopyTalent.auditDate  <=   #{dcaBCopyTalent.auditDateTo}

     and dcaBCopyTalent.auditSuggestion like concat('%', #{ dcaBCopyTalent.auditSuggestion} ,'%')
