mapper.update:
update dca_b_copy_teacherprize
dca_year=#{dcaYear}, file_id=#{fileId}, display_index=#{displayIndex}, file_url=#{fileUrl}, user_account_name=#{userAccountName}, user_account=#{userAccount}, prize_name=#{prizeName}, prize_grade=#{prizeGrade}, prize_date=#{prizeDate}, ranknum=#{ranknum}, state=#{state}, IS_DELETEMARK=#{isDeletemark}, CREATE_TIME=#{createTime}, MODIFY_TIME=#{modifyTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId}, auditMan=#{auditMan}, auditManName=#{auditManName}, auditDate=#{auditDate}, auditSuggestion=#{auditSuggestion}, IsUse=#{IsUse},
where id=#{id}

mapper.select:
select  *  from dca_b_copy_teacherprize
where  1=1
     and dcaBCopyTeacherprize.id =#{ dcaBCopyTeacherprize.id} 
     and dcaBCopyTeacherprize.dca_year like concat('%', #{ dcaBCopyTeacherprize.dcaYear} ,'%')
     and dcaBCopyTeacherprize.file_id like concat('%', #{ dcaBCopyTeacherprize.fileId} ,'%')
 and dcaBCopyTeacherprize.display_index =#{ dcaBCopyTeacherprize.displayIndex} 
     and dcaBCopyTeacherprize.file_url like concat('%', #{ dcaBCopyTeacherprize.fileUrl} ,'%')
     and dcaBCopyTeacherprize.user_account_name like concat('%', #{ dcaBCopyTeacherprize.userAccountName} ,'%')
     and dcaBCopyTeacherprize.user_account like concat('%', #{ dcaBCopyTeacherprize.userAccount} ,'%')
     and dcaBCopyTeacherprize.prize_name like concat('%', #{ dcaBCopyTeacherprize.prizeName} ,'%')
     and dcaBCopyTeacherprize.prize_grade like concat('%', #{ dcaBCopyTeacherprize.prizeGrade} ,'%')

    AND dcaBCopyTeacherprize.prize_date  >=   #{dcaBCopyTeacherprize.prizeDateFrom}


    AND dcaBCopyTeacherprize.prize_date  <=   #{dcaBCopyTeacherprize.prizeDateTo}

     and dcaBCopyTeacherprize.ranknum like concat('%', #{ dcaBCopyTeacherprize.ranknum} ,'%')
 and dcaBCopyTeacherprize.state =#{ dcaBCopyTeacherprize.state} 
 and dcaBCopyTeacherprize.IS_DELETEMARK =#{ dcaBCopyTeacherprize.isDeletemark} 

    AND dcaBCopyTeacherprize.CREATE_TIME  >=   #{dcaBCopyTeacherprize.createTimeFrom}


    AND dcaBCopyTeacherprize.CREATE_TIME  <=   #{dcaBCopyTeacherprize.createTimeTo}


    AND dcaBCopyTeacherprize.MODIFY_TIME  >=   #{dcaBCopyTeacherprize.modifyTimeFrom}


    AND dcaBCopyTeacherprize.MODIFY_TIME  <=   #{dcaBCopyTeacherprize.modifyTimeTo}

     and dcaBCopyTeacherprize.auditMan like concat('%', #{ dcaBCopyTeacherprize.auditMan} ,'%')
     and dcaBCopyTeacherprize.auditManName like concat('%', #{ dcaBCopyTeacherprize.auditManName} ,'%')

    AND dcaBCopyTeacherprize.auditDate  >=   #{dcaBCopyTeacherprize.auditDateFrom}


    AND dcaBCopyTeacherprize.auditDate  <=   #{dcaBCopyTeacherprize.auditDateTo}

     and dcaBCopyTeacherprize.auditSuggestion like concat('%', #{ dcaBCopyTeacherprize.auditSuggestion} ,'%')
