mapper.update:
update dca_b_copy_teacherqualify
dca_year=#{dcaYear}, file_id=#{fileId}, display_index=#{displayIndex}, file_url=#{fileUrl}, user_account_name=#{userAccountName}, user_account=#{userAccount}, tq_code=#{tqCode}, tq_receive_date=#{tqReceiveDate}, state=#{state}, IS_DELETEMARK=#{isDeletemark}, CREATE_TIME=#{createTime}, MODIFY_TIME=#{modifyTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId}, auditMan=#{auditMan}, auditManName=#{auditManName}, auditDate=#{auditDate}, auditSuggestion=#{auditSuggestion}, IsUse=#{IsUse},
where id=#{id}

mapper.select:
select  *  from dca_b_copy_teacherqualify
where  1=1
     and dcaBCopyTeacherqualify.id =#{ dcaBCopyTeacherqualify.id} 
     and dcaBCopyTeacherqualify.dca_year like concat('%', #{ dcaBCopyTeacherqualify.dcaYear} ,'%')
     and dcaBCopyTeacherqualify.file_id like concat('%', #{ dcaBCopyTeacherqualify.fileId} ,'%')
 and dcaBCopyTeacherqualify.display_index =#{ dcaBCopyTeacherqualify.displayIndex} 
     and dcaBCopyTeacherqualify.file_url like concat('%', #{ dcaBCopyTeacherqualify.fileUrl} ,'%')
     and dcaBCopyTeacherqualify.user_account_name like concat('%', #{ dcaBCopyTeacherqualify.userAccountName} ,'%')
     and dcaBCopyTeacherqualify.user_account like concat('%', #{ dcaBCopyTeacherqualify.userAccount} ,'%')
     and dcaBCopyTeacherqualify.tq_code like concat('%', #{ dcaBCopyTeacherqualify.tqCode} ,'%')

    AND dcaBCopyTeacherqualify.tq_receive_date  >=   #{dcaBCopyTeacherqualify.tqReceiveDateFrom}


    AND dcaBCopyTeacherqualify.tq_receive_date  <=   #{dcaBCopyTeacherqualify.tqReceiveDateTo}

 and dcaBCopyTeacherqualify.state =#{ dcaBCopyTeacherqualify.state} 
 and dcaBCopyTeacherqualify.IS_DELETEMARK =#{ dcaBCopyTeacherqualify.isDeletemark} 

    AND dcaBCopyTeacherqualify.CREATE_TIME  >=   #{dcaBCopyTeacherqualify.createTimeFrom}


    AND dcaBCopyTeacherqualify.CREATE_TIME  <=   #{dcaBCopyTeacherqualify.createTimeTo}


    AND dcaBCopyTeacherqualify.MODIFY_TIME  >=   #{dcaBCopyTeacherqualify.modifyTimeFrom}


    AND dcaBCopyTeacherqualify.MODIFY_TIME  <=   #{dcaBCopyTeacherqualify.modifyTimeTo}

     and dcaBCopyTeacherqualify.auditMan like concat('%', #{ dcaBCopyTeacherqualify.auditMan} ,'%')
     and dcaBCopyTeacherqualify.auditManName like concat('%', #{ dcaBCopyTeacherqualify.auditManName} ,'%')

    AND dcaBCopyTeacherqualify.auditDate  >=   #{dcaBCopyTeacherqualify.auditDateFrom}


    AND dcaBCopyTeacherqualify.auditDate  <=   #{dcaBCopyTeacherqualify.auditDateTo}

     and dcaBCopyTeacherqualify.auditSuggestion like concat('%', #{ dcaBCopyTeacherqualify.auditSuggestion} ,'%')
