mapper.update:
update dca_b_copy_teacheryj
dca_year=#{dcaYear}, file_id=#{fileId}, file_url=#{fileUrl}, user_account_name=#{userAccountName}, user_account=#{userAccount}, teach_name=#{teachName}, teach_date=#{teachDate}, teach_content=#{teachContent}, rank_num=#{rankNum}, state=#{state}, IS_DELETEMARK=#{isDeletemark}, CREATE_TIME=#{createTime}, MODIFY_TIME=#{modifyTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId}, auditMan=#{auditMan}, auditManName=#{auditManName}, auditDate=#{auditDate}, auditSuggestion=#{auditSuggestion}, IsUse=#{IsUse}, display_index=#{displayIndex},
where id=#{id}

mapper.select:
select  *  from dca_b_copy_teacheryj
where  1=1
     and dca_b_copy_teacheryj.id =#{ dcaBCopyTeacheryj.id} 
     and dca_b_copy_teacheryj.dca_year like concat('%', #{ dcaBCopyTeacheryj.dcaYear} ,'%')
     and dca_b_copy_teacheryj.file_id like concat('%', #{ dcaBCopyTeacheryj.fileId} ,'%')
     and dca_b_copy_teacheryj.file_url like concat('%', #{ dcaBCopyTeacheryj.fileUrl} ,'%')
     and dca_b_copy_teacheryj.user_account_name like concat('%', #{ dcaBCopyTeacheryj.userAccountName} ,'%')
     and dca_b_copy_teacheryj.user_account like concat('%', #{ dcaBCopyTeacheryj.userAccount} ,'%')
     and dca_b_copy_teacheryj.teach_name like concat('%', #{ dcaBCopyTeacheryj.teachName} ,'%')

    AND dca_b_copy_teacheryj.teach_date  >=   #{dcaBCopyTeacheryj.teachDateFrom}


    AND dca_b_copy_teacheryj.teach_date  <=   #{dcaBCopyTeacheryj.teachDateTo}

     and dca_b_copy_teacheryj.teach_content like concat('%', #{ dcaBCopyTeacheryj.teachContent} ,'%')
 and dca_b_copy_teacheryj.rank_num =#{ dcaBCopyTeacheryj.rankNum} 
 and dca_b_copy_teacheryj.state =#{ dcaBCopyTeacheryj.state} 
 and dca_b_copy_teacheryj.IS_DELETEMARK =#{ dcaBCopyTeacheryj.isDeletemark} 

    AND dca_b_copy_teacheryj.CREATE_TIME  >=   #{dcaBCopyTeacheryj.createTimeFrom}


    AND dca_b_copy_teacheryj.CREATE_TIME  <=   #{dcaBCopyTeacheryj.createTimeTo}


    AND dca_b_copy_teacheryj.MODIFY_TIME  >=   #{dcaBCopyTeacheryj.modifyTimeFrom}


    AND dca_b_copy_teacheryj.MODIFY_TIME  <=   #{dcaBCopyTeacheryj.modifyTimeTo}

     and dca_b_copy_teacheryj.auditMan like concat('%', #{ dcaBCopyTeacheryj.auditMan} ,'%')
     and dca_b_copy_teacheryj.auditManName like concat('%', #{ dcaBCopyTeacheryj.auditManName} ,'%')

    AND dca_b_copy_teacheryj.auditDate  >=   #{dcaBCopyTeacheryj.auditDateFrom}


    AND dca_b_copy_teacheryj.auditDate  <=   #{dcaBCopyTeacheryj.auditDateTo}

     and dca_b_copy_teacheryj.auditSuggestion like concat('%', #{ dcaBCopyTeacheryj.auditSuggestion} ,'%')
 and dca_b_copy_teacheryj.display_index =#{ dcaBCopyTeacheryj.displayIndex}
