mapper.update:
update dca_b_copy_teachtalent
dca_year=#{dcaYear}, file_id=#{fileId}, display_index=#{displayIndex}, file_url=#{fileUrl}, user_account_name=#{userAccountName}, user_account=#{userAccount}, teachtalet_name=#{teachtaletName}, teachtalent_start_date=#{teachtalentStartDate}, teachtalent_end_date=#{teachtalentEndDate}, student_number=#{studentNumber}, week_time=#{weekTime}, total_time=#{totalTime}, state=#{state}, IS_DELETEMARK=#{isDeletemark}, CREATE_TIME=#{createTime}, MODIFY_TIME=#{modifyTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId}, auditMan=#{auditMan}, auditManName=#{auditManName}, auditDate=#{auditDate}, auditSuggestion=#{auditSuggestion}, IsUse=#{IsUse}, note=#{note},
where id=#{id}

mapper.select:
select  *  from dca_b_copy_teachtalent
where  1=1
     and dcaBCopyTeachtalent.id =#{ dcaBCopyTeachtalent.id} 
     and dcaBCopyTeachtalent.dca_year like concat('%', #{ dcaBCopyTeachtalent.dcaYear} ,'%')
     and dcaBCopyTeachtalent.file_id like concat('%', #{ dcaBCopyTeachtalent.fileId} ,'%')
 and dcaBCopyTeachtalent.display_index =#{ dcaBCopyTeachtalent.displayIndex} 
     and dcaBCopyTeachtalent.file_url like concat('%', #{ dcaBCopyTeachtalent.fileUrl} ,'%')
     and dcaBCopyTeachtalent.user_account_name like concat('%', #{ dcaBCopyTeachtalent.userAccountName} ,'%')
     and dcaBCopyTeachtalent.user_account like concat('%', #{ dcaBCopyTeachtalent.userAccount} ,'%')
     and dcaBCopyTeachtalent.teachtalet_name like concat('%', #{ dcaBCopyTeachtalent.teachtaletName} ,'%')

    AND dcaBCopyTeachtalent.teachtalent_start_date  >=   #{dcaBCopyTeachtalent.teachtalentStartDateFrom}


    AND dcaBCopyTeachtalent.teachtalent_start_date  <=   #{dcaBCopyTeachtalent.teachtalentStartDateTo}


    AND dcaBCopyTeachtalent.teachtalent_end_date  >=   #{dcaBCopyTeachtalent.teachtalentEndDateFrom}


    AND dcaBCopyTeachtalent.teachtalent_end_date  <=   #{dcaBCopyTeachtalent.teachtalentEndDateTo}

 and dcaBCopyTeachtalent.student_number =#{ dcaBCopyTeachtalent.studentNumber} 
 and dcaBCopyTeachtalent.week_time =#{ dcaBCopyTeachtalent.weekTime} 
 and dcaBCopyTeachtalent.total_time =#{ dcaBCopyTeachtalent.totalTime} 
 and dcaBCopyTeachtalent.state =#{ dcaBCopyTeachtalent.state} 
 and dcaBCopyTeachtalent.IS_DELETEMARK =#{ dcaBCopyTeachtalent.isDeletemark} 

    AND dcaBCopyTeachtalent.CREATE_TIME  >=   #{dcaBCopyTeachtalent.createTimeFrom}


    AND dcaBCopyTeachtalent.CREATE_TIME  <=   #{dcaBCopyTeachtalent.createTimeTo}


    AND dcaBCopyTeachtalent.MODIFY_TIME  >=   #{dcaBCopyTeachtalent.modifyTimeFrom}


    AND dcaBCopyTeachtalent.MODIFY_TIME  <=   #{dcaBCopyTeachtalent.modifyTimeTo}

     and dcaBCopyTeachtalent.auditMan like concat('%', #{ dcaBCopyTeachtalent.auditMan} ,'%')
     and dcaBCopyTeachtalent.auditManName like concat('%', #{ dcaBCopyTeachtalent.auditManName} ,'%')

    AND dcaBCopyTeachtalent.auditDate  >=   #{dcaBCopyTeachtalent.auditDateFrom}


    AND dcaBCopyTeachtalent.auditDate  <=   #{dcaBCopyTeachtalent.auditDateTo}

     and dcaBCopyTeachtalent.auditSuggestion like concat('%', #{ dcaBCopyTeachtalent.auditSuggestion} ,'%')
     and dcaBCopyTeachtalent.note like concat('%', #{ dcaBCopyTeachtalent.note} ,'%')
