mapper.update:
update dca_b_copy_turtor
dca_year=#{dcaYear}, file_id=#{fileId}, file_url=#{fileUrl}, user_account_name=#{userAccountName}, user_account=#{userAccount}, turtor_main=#{turtorMain}, tutor_content=#{tutorContent}, state=#{state}, IS_DELETEMARK=#{isDeletemark}, CREATE_TIME=#{createTime}, MODIFY_TIME=#{modifyTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId}, auditMan=#{auditMan}, auditManName=#{auditManName}, auditDate=#{auditDate}, auditSuggestion=#{auditSuggestion}, IsUse=#{IsUse}, display_index=#{displayIndex},
where id=#{id}

mapper.select:
select  *  from dca_b_copy_turtor
where  1=1
     and dcaBCopyTurtor.id =#{ dcaBCopyTurtor.id} 
     and dcaBCopyTurtor.dca_year like concat('%', #{ dcaBCopyTurtor.dcaYear} ,'%')
     and dcaBCopyTurtor.file_id like concat('%', #{ dcaBCopyTurtor.fileId} ,'%')
     and dcaBCopyTurtor.file_url like concat('%', #{ dcaBCopyTurtor.fileUrl} ,'%')
     and dcaBCopyTurtor.user_account_name like concat('%', #{ dcaBCopyTurtor.userAccountName} ,'%')
     and dcaBCopyTurtor.user_account like concat('%', #{ dcaBCopyTurtor.userAccount} ,'%')
     and dcaBCopyTurtor.turtor_main like concat('%', #{ dcaBCopyTurtor.turtorMain} ,'%')
     and dcaBCopyTurtor.tutor_content like concat('%', #{ dcaBCopyTurtor.tutorContent} ,'%')
 and dcaBCopyTurtor.state =#{ dcaBCopyTurtor.state} 
 and dcaBCopyTurtor.IS_DELETEMARK =#{ dcaBCopyTurtor.isDeletemark} 

    AND dcaBCopyTurtor.CREATE_TIME  >=   #{dcaBCopyTurtor.createTimeFrom}


    AND dcaBCopyTurtor.CREATE_TIME  <=   #{dcaBCopyTurtor.createTimeTo}


    AND dcaBCopyTurtor.MODIFY_TIME  >=   #{dcaBCopyTurtor.modifyTimeFrom}


    AND dcaBCopyTurtor.MODIFY_TIME  <=   #{dcaBCopyTurtor.modifyTimeTo}

     and dcaBCopyTurtor.auditMan like concat('%', #{ dcaBCopyTurtor.auditMan} ,'%')
     and dcaBCopyTurtor.auditManName like concat('%', #{ dcaBCopyTurtor.auditManName} ,'%')

    AND dcaBCopyTurtor.auditDate  >=   #{dcaBCopyTurtor.auditDateFrom}


    AND dcaBCopyTurtor.auditDate  <=   #{dcaBCopyTurtor.auditDateTo}

     and dcaBCopyTurtor.auditSuggestion like concat('%', #{ dcaBCopyTurtor.auditSuggestion} ,'%')
 and dcaBCopyTurtor.display_index =#{ dcaBCopyTurtor.displayIndex}
