mapper.update:
update dca_b_copy_undergraduate
dca_year=#{dcaYear}, file_id=#{fileId}, file_url=#{fileUrl}, user_account_name=#{userAccountName}, user_account=#{userAccount}, course_name=#{courseName}, ug_start_date=#{ugStartDate}, ug_end_date=#{ugEndDate}, course_type=#{courseType}, student_number=#{studentNumber}, total_time=#{totalTime}, person_time=#{personTime}, teach_score=#{teachScore}, state=#{state}, IS_DELETEMARK=#{isDeletemark}, CREATE_TIME=#{createTime}, MODIFY_TIME=#{modifyTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId}, auditMan=#{auditMan}, auditManName=#{auditManName}, auditDate=#{auditDate}, auditSuggestion=#{auditSuggestion}, IsUse=#{IsUse}, display_index=#{displayIndex},
where id=#{id}

mapper.select:
select  *  from dca_b_copy_undergraduate
where  1=1
     and dcaBCopyUndergraduate.id =#{ dcaBCopyUndergraduate.id} 
     and dcaBCopyUndergraduate.dca_year like concat('%', #{ dcaBCopyUndergraduate.dcaYear} ,'%')
     and dcaBCopyUndergraduate.file_id like concat('%', #{ dcaBCopyUndergraduate.fileId} ,'%')
     and dcaBCopyUndergraduate.file_url like concat('%', #{ dcaBCopyUndergraduate.fileUrl} ,'%')
     and dcaBCopyUndergraduate.user_account_name like concat('%', #{ dcaBCopyUndergraduate.userAccountName} ,'%')
     and dcaBCopyUndergraduate.user_account like concat('%', #{ dcaBCopyUndergraduate.userAccount} ,'%')
     and dcaBCopyUndergraduate.course_name like concat('%', #{ dcaBCopyUndergraduate.courseName} ,'%')

    AND dcaBCopyUndergraduate.ug_start_date  >=   #{dcaBCopyUndergraduate.ugStartDateFrom}


    AND dcaBCopyUndergraduate.ug_start_date  <=   #{dcaBCopyUndergraduate.ugStartDateTo}


    AND dcaBCopyUndergraduate.ug_end_date  >=   #{dcaBCopyUndergraduate.ugEndDateFrom}


    AND dcaBCopyUndergraduate.ug_end_date  <=   #{dcaBCopyUndergraduate.ugEndDateTo}

     and dcaBCopyUndergraduate.course_type like concat('%', #{ dcaBCopyUndergraduate.courseType} ,'%')
 and dcaBCopyUndergraduate.student_number =#{ dcaBCopyUndergraduate.studentNumber} 
     and dcaBCopyUndergraduate.teach_score like concat('%', #{ dcaBCopyUndergraduate.teachScore} ,'%')
 and dcaBCopyUndergraduate.state =#{ dcaBCopyUndergraduate.state} 
 and dcaBCopyUndergraduate.IS_DELETEMARK =#{ dcaBCopyUndergraduate.isDeletemark} 

    AND dcaBCopyUndergraduate.CREATE_TIME  >=   #{dcaBCopyUndergraduate.createTimeFrom}


    AND dcaBCopyUndergraduate.CREATE_TIME  <=   #{dcaBCopyUndergraduate.createTimeTo}


    AND dcaBCopyUndergraduate.MODIFY_TIME  >=   #{dcaBCopyUndergraduate.modifyTimeFrom}


    AND dcaBCopyUndergraduate.MODIFY_TIME  <=   #{dcaBCopyUndergraduate.modifyTimeTo}

     and dcaBCopyUndergraduate.auditMan like concat('%', #{ dcaBCopyUndergraduate.auditMan} ,'%')
     and dcaBCopyUndergraduate.auditManName like concat('%', #{ dcaBCopyUndergraduate.auditManName} ,'%')

    AND dcaBCopyUndergraduate.auditDate  >=   #{dcaBCopyUndergraduate.auditDateFrom}


    AND dcaBCopyUndergraduate.auditDate  <=   #{dcaBCopyUndergraduate.auditDateTo}

     and dcaBCopyUndergraduate.auditSuggestion like concat('%', #{ dcaBCopyUndergraduate.auditSuggestion} ,'%')
 and dcaBCopyUndergraduate.display_index =#{ dcaBCopyUndergraduate.displayIndex}
