mapper.update:
update dca_b_copy_undergraduateprize
dca_year=#{dcaYear}, file_id=#{fileId}, file_url=#{fileUrl}, user_account_name=#{userAccountName}, user_account=#{userAccount}, sp_project_name=#{spProjectName}, sr_project_grade=#{srProjectGrade}, sr_project_level=#{srProjectLevel}, sr_prize_dept=#{srPrizeDept}, sr_prize_date=#{srPrizeDate}, sr_prize_ranknum=#{srPrizeRanknum}, state=#{state}, IS_DELETEMARK=#{isDeletemark}, CREATE_TIME=#{createTime}, MODIFY_TIME=#{modifyTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId}, auditMan=#{auditMan}, auditManName=#{auditManName}, auditDate=#{auditDate}, auditSuggestion=#{auditSuggestion}, IsUse=#{IsUse}, display_index=#{displayIndex},
where id=#{id}

mapper.select:
select  *  from dca_b_copy_undergraduateprize
where  1=1
     and dcaBCopyUndergraduateprize.id =#{ dcaBCopyUndergraduateprize.id} 
     and dcaBCopyUndergraduateprize.dca_year like concat('%', #{ dcaBCopyUndergraduateprize.dcaYear} ,'%')
     and dcaBCopyUndergraduateprize.file_id like concat('%', #{ dcaBCopyUndergraduateprize.fileId} ,'%')
     and dcaBCopyUndergraduateprize.file_url like concat('%', #{ dcaBCopyUndergraduateprize.fileUrl} ,'%')
     and dcaBCopyUndergraduateprize.user_account_name like concat('%', #{ dcaBCopyUndergraduateprize.userAccountName} ,'%')
     and dcaBCopyUndergraduateprize.user_account like concat('%', #{ dcaBCopyUndergraduateprize.userAccount} ,'%')
     and dcaBCopyUndergraduateprize.sp_project_name like concat('%', #{ dcaBCopyUndergraduateprize.spProjectName} ,'%')
     and dcaBCopyUndergraduateprize.sr_project_grade like concat('%', #{ dcaBCopyUndergraduateprize.srProjectGrade} ,'%')
     and dcaBCopyUndergraduateprize.sr_project_level like concat('%', #{ dcaBCopyUndergraduateprize.srProjectLevel} ,'%')
     and dcaBCopyUndergraduateprize.sr_prize_dept like concat('%', #{ dcaBCopyUndergraduateprize.srPrizeDept} ,'%')

    AND dcaBCopyUndergraduateprize.sr_prize_date  >=   #{dcaBCopyUndergraduateprize.srPrizeDateFrom}


    AND dcaBCopyUndergraduateprize.sr_prize_date  <=   #{dcaBCopyUndergraduateprize.srPrizeDateTo}

 and dcaBCopyUndergraduateprize.sr_prize_ranknum =#{ dcaBCopyUndergraduateprize.srPrizeRanknum} 
 and dcaBCopyUndergraduateprize.state =#{ dcaBCopyUndergraduateprize.state} 
 and dcaBCopyUndergraduateprize.IS_DELETEMARK =#{ dcaBCopyUndergraduateprize.isDeletemark} 

    AND dcaBCopyUndergraduateprize.CREATE_TIME  >=   #{dcaBCopyUndergraduateprize.createTimeFrom}


    AND dcaBCopyUndergraduateprize.CREATE_TIME  <=   #{dcaBCopyUndergraduateprize.createTimeTo}


    AND dcaBCopyUndergraduateprize.MODIFY_TIME  >=   #{dcaBCopyUndergraduateprize.modifyTimeFrom}


    AND dcaBCopyUndergraduateprize.MODIFY_TIME  <=   #{dcaBCopyUndergraduateprize.modifyTimeTo}

     and dcaBCopyUndergraduateprize.auditMan like concat('%', #{ dcaBCopyUndergraduateprize.auditMan} ,'%')
     and dcaBCopyUndergraduateprize.auditManName like concat('%', #{ dcaBCopyUndergraduateprize.auditManName} ,'%')

    AND dcaBCopyUndergraduateprize.auditDate  >=   #{dcaBCopyUndergraduateprize.auditDateFrom}


    AND dcaBCopyUndergraduateprize.auditDate  <=   #{dcaBCopyUndergraduateprize.auditDateTo}

     and dcaBCopyUndergraduateprize.auditSuggestion like concat('%', #{ dcaBCopyUndergraduateprize.auditSuggestion} ,'%')
 and dcaBCopyUndergraduateprize.display_index =#{ dcaBCopyUndergraduateprize.displayIndex}
