mapper.update:
update dca_b_copy_user
user_account_name=#{userAccountName}, user_account=#{userAccount}, ks=#{ks}, xl=#{xl}, telephone=#{telephone}, dept_name=#{deptName}, position_name=#{positionName}, np_position_name=#{npPositionName}, sex_name=#{sexName}, birthday=#{birthday}, school_date=#{schoolDate}, zyjsgw=#{zyjsgw}, zyjsgw_lc=#{zyjsgwLc}, xcszyjzc=#{xcszyjzc}, appointed_date=#{appointedDate}, patent_ranknum=#{patentRanknum}, is_authority=#{isAuthority}, file_id=#{fileId}, is_zhuanrang=#{isZhuanrang}, file_url=#{fileUrl}, patent_good=#{patentGood}, state=#{state}, IS_DELETEMARK=#{isDeletemark}, CREATE_TIME=#{createTime}, display_index=#{displayIndex}, MODIFY_TIME=#{modifyTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId}, auditMan=#{auditMan}, auditManName=#{auditManName}, auditDate=#{auditDate}, auditSuggestion=#{auditSuggestion}, IsUse=#{IsUse}, appointed_date_lc=#{appointedDateLc}, file_id_lc=#{fileIdLc}, file_url_lc=#{fileUrlLc}, yuangongzu=#{yuangongzu}, xrgwjb=#{xrgwjb}, xrgwjbprsj=#{xrgwjbprsj}, djrdzzw=#{djrdzzw}, dca_year=#{dcaYear}, is_chujikh=#{isChujikh}, chujikh_date=#{chujikhDate}, is_zhongjikh=#{isZhongjikh}, zhongjikh_date=#{zhongjikhDate}, political_status=#{politicalStatus}, staff_grade=#{staffGrade}, id_card=#{idCard}, staff_date=#{staffDate}, picture_id=#{pictureId}, picture_url=#{pictureUrl},
where id=#{id}

mapper.select:
select  *  from dca_b_copy_user
where  1=1
     and dca_b_copy_user.id =#{ dcaBCopyUser.id} 
     and dca_b_copy_user.user_account_name like concat('%', #{ dcaBCopyUser.userAccountName} ,'%')
     and dca_b_copy_user.user_account like concat('%', #{ dcaBCopyUser.userAccount} ,'%')
     and dca_b_copy_user.ks like concat('%', #{ dcaBCopyUser.ks} ,'%')
     and dca_b_copy_user.xl like concat('%', #{ dcaBCopyUser.xl} ,'%')
     and dca_b_copy_user.telephone like concat('%', #{ dcaBCopyUser.telephone} ,'%')
     and dca_b_copy_user.dept_name like concat('%', #{ dcaBCopyUser.deptName} ,'%')
     and dca_b_copy_user.position_name like concat('%', #{ dcaBCopyUser.positionName} ,'%')
     and dca_b_copy_user.np_position_name like concat('%', #{ dcaBCopyUser.npPositionName} ,'%')
     and dca_b_copy_user.sex_name like concat('%', #{ dcaBCopyUser.sexName} ,'%')

    AND dca_b_copy_user.birthday  >=   #{dcaBCopyUser.birthdayFrom}


    AND dca_b_copy_user.birthday  <=   #{dcaBCopyUser.birthdayTo}


    AND dca_b_copy_user.school_date  >=   #{dcaBCopyUser.schoolDateFrom}


    AND dca_b_copy_user.school_date  <=   #{dcaBCopyUser.schoolDateTo}

     and dca_b_copy_user.zyjsgw like concat('%', #{ dcaBCopyUser.zyjsgw} ,'%')
     and dca_b_copy_user.zyjsgw_lc like concat('%', #{ dcaBCopyUser.zyjsgwLc} ,'%')
     and dca_b_copy_user.xcszyjzc like concat('%', #{ dcaBCopyUser.xcszyjzc} ,'%')

    AND dca_b_copy_user.appointed_date  >=   #{dcaBCopyUser.appointedDateFrom}


    AND dca_b_copy_user.appointed_date  <=   #{dcaBCopyUser.appointedDateTo}

 and dca_b_copy_user.patent_ranknum =#{ dcaBCopyUser.patentRanknum} 
     and dca_b_copy_user.is_authority like concat('%', #{ dcaBCopyUser.isAuthority} ,'%')
     and dca_b_copy_user.file_id like concat('%', #{ dcaBCopyUser.fileId} ,'%')
     and dca_b_copy_user.is_zhuanrang like concat('%', #{ dcaBCopyUser.isZhuanrang} ,'%')
     and dca_b_copy_user.file_url like concat('%', #{ dcaBCopyUser.fileUrl} ,'%')
     and dca_b_copy_user.patent_good like concat('%', #{ dcaBCopyUser.patentGood} ,'%')
 and dca_b_copy_user.state =#{ dcaBCopyUser.state} 
 and dca_b_copy_user.IS_DELETEMARK =#{ dcaBCopyUser.isDeletemark} 

    AND dca_b_copy_user.CREATE_TIME  >=   #{dcaBCopyUser.createTimeFrom}


    AND dca_b_copy_user.CREATE_TIME  <=   #{dcaBCopyUser.createTimeTo}

 and dca_b_copy_user.display_index =#{ dcaBCopyUser.displayIndex} 

    AND dca_b_copy_user.MODIFY_TIME  >=   #{dcaBCopyUser.modifyTimeFrom}


    AND dca_b_copy_user.MODIFY_TIME  <=   #{dcaBCopyUser.modifyTimeTo}

     and dca_b_copy_user.auditMan like concat('%', #{ dcaBCopyUser.auditMan} ,'%')
     and dca_b_copy_user.auditManName like concat('%', #{ dcaBCopyUser.auditManName} ,'%')

    AND dca_b_copy_user.auditDate  >=   #{dcaBCopyUser.auditDateFrom}


    AND dca_b_copy_user.auditDate  <=   #{dcaBCopyUser.auditDateTo}

     and dca_b_copy_user.auditSuggestion like concat('%', #{ dcaBCopyUser.auditSuggestion} ,'%')

    AND dca_b_copy_user.appointed_date_lc  >=   #{dcaBCopyUser.appointedDateLcFrom}


    AND dca_b_copy_user.appointed_date_lc  <=   #{dcaBCopyUser.appointedDateLcTo}

     and dca_b_copy_user.file_id_lc like concat('%', #{ dcaBCopyUser.fileIdLc} ,'%')
     and dca_b_copy_user.file_url_lc like concat('%', #{ dcaBCopyUser.fileUrlLc} ,'%')
     and dca_b_copy_user.yuangongzu like concat('%', #{ dcaBCopyUser.yuangongzu} ,'%')
     and dca_b_copy_user.xrgwjb like concat('%', #{ dcaBCopyUser.xrgwjb} ,'%')

    AND dca_b_copy_user.xrgwjbprsj  >=   #{dcaBCopyUser.xrgwjbprsjFrom}


    AND dca_b_copy_user.xrgwjbprsj  <=   #{dcaBCopyUser.xrgwjbprsjTo}

     and dca_b_copy_user.djrdzzw like concat('%', #{ dcaBCopyUser.djrdzzw} ,'%')
     and dca_b_copy_user.dca_year like concat('%', #{ dcaBCopyUser.dcaYear} ,'%')

    AND dca_b_copy_user.chujikh_date  >=   #{dcaBCopyUser.chujikhDateFrom}


    AND dca_b_copy_user.chujikh_date  <=   #{dcaBCopyUser.chujikhDateTo}


    AND dca_b_copy_user.zhongjikh_date  >=   #{dcaBCopyUser.zhongjikhDateFrom}


    AND dca_b_copy_user.zhongjikh_date  <=   #{dcaBCopyUser.zhongjikhDateTo}

     and dca_b_copy_user.political_status like concat('%', #{ dcaBCopyUser.politicalStatus} ,'%')
     and dca_b_copy_user.staff_grade like concat('%', #{ dcaBCopyUser.staffGrade} ,'%')
     and dca_b_copy_user.id_card like concat('%', #{ dcaBCopyUser.idCard} ,'%')

    AND dca_b_copy_user.staff_date  >=   #{dcaBCopyUser.staffDateFrom}


    AND dca_b_copy_user.staff_date  <=   #{dcaBCopyUser.staffDateTo}

     and dca_b_copy_user.picture_id like concat('%', #{ dcaBCopyUser.pictureId} ,'%')
     and dca_b_copy_user.picture_url like concat('%', #{ dcaBCopyUser.pictureUrl} ,'%')
